const mockUsers: User[] = [];

// Register user
export const register = asyncHandler(async (req: Request, res: Response) => {
  const { email, password, firstName, lastName, phone, role } = req.validatedData as RegisterRequestData;

  // Check if user already exists
  const existingUser = mockUsers.find(user => user.email === email);
  if (existingUser) {
    throw new AppError('User already exists with this email', 409);
  }

  // Hash password
  const hashedPassword = await bcrypt.hash(password, config.security.bcryptSaltRounds);

  // Create user
  const user: User = {
    id: uuidv4(),
    email,
    firstName,
    lastName,
    phone,
    role,
    isActive: true,
    createdAt: new Date(),
    updatedAt: new Date(),
  };

  // Store user (with hashed password)
  mockUsers.push({ ...user, password: hashedPassword } as any);

  // Generate tokens
  const { accessToken, refreshToken } = generateTokens({
    userId: user.id,
    email: user.email,
    role: user.role,
  });

  logger.info('User registered successfully', {
    userId: user.id,
    email: user.email,
    role: user.role,
  });

  const response: ApiResponse = {
