// Get all instructors with filtering and pagination
export const getInstructors = asyncHandler(async (req: Request, res: Response) => {
  const { 
    page = 1, 
    limit = 10, 
    sortBy = 'rating', 
    sortOrder = 'desc',
    specialty,
    location,
    priceMin,
    priceMax,
    rating,
    language
  } = req.validatedQuery || {};

  let filteredInstructors = [...mockInstructors];

  // Apply filters
  if (specialty) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.specialties.includes(specialty)
    );
  }

  if (location) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.preferredLocations.some(loc => 
        loc.toLowerCase().includes(location.toLowerCase())
      )
    );
  }

  if (priceMin) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.hourlyRate >= priceMin
    );
  }

  if (priceMax) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.hourlyRate <= priceMax
    );
  }

  if (rating) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.rating >= rating
    );
  }

  if (language) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.languages.some(lang => 
        lang.toLowerCase().includes(language.toLowerCase())
      )
    );
  }

  // Apply sorting
  filteredInstructors.sort((a, b) => {
    const aValue = a[sortBy as keyof Instructor];
    const bValue = b[sortBy as keyof Instructor];

    if (sortOrder === 'desc') {
      return bValue > aValue ? 1 : -1;
    }
    return aValue > bValue ? 1 : -1;
  });

  // Apply pagination
  const startIndex = (page - 1) * limit;
  const endIndex = startIndex + limit;
  const paginatedInstructors = filteredInstructors.slice(startIndex, endIndex);

  const response: ApiResponse = {
    success: true,
    message: 'Instructors retrieved successfully',
    data: {
      instructors: paginatedInstructors,
    },
    meta: {
      page,
      limit,
      total: filteredInstructors.length,
      totalPages: Math.ceil(filteredInstructors.length / limit),
    },
  };

  res.json(response);
});

// Get instructor by ID
export const getInstructorById = asyncHandler(async (req: Request, res: Response) => {
  const { id } = req.params;

  const instructor = mockInstructors.find(i => i.id === id);
  if (!instructor) {
    throw new AppError('Instructor not found', 404);
  }

  const response: ApiResponse = {
    success: true,
    message: 'Instructor retrieved successfully',
    data: {
      instructor,
    },
  };

  res.json(response);
});

// Create instructor profile (for existing users)
export const createInstructorProfile = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const profileData = req.validatedData;

  // Check if instructor profile already exists
  const existingInstructor = mockInstructors.find(i => i.id === userId);
  if (existingInstructor) {
    throw new AppError('Instructor profile already exists', 409);
  }

  // Create instructor profile
  const instructor: Instructor = {
    id: userId,
    email: req.user!.email,
    firstName: 'John', // This would come from user profile
