    rating: 0,
    reviewCount: 0,
    isAvailable: profileData.isAvailable !== undefined ? profileData.isAvailable : true,
    preferredLocations: profileData.preferredLocations || [],
  };

  mockInstructors.push(instructor);

  logger.info('Instructor profile created', { 
    instructorId: instructor.id,
    specialties: instructor.specialties,
    hourlyRate: instructor.hourlyRate,
  });

  const response: ApiResponse = {
    success: true,
    message: 'Instructor profile created successfully',
    data: {
      instructor,
    },
  };

  res.status(201).json(response);
});

// Update instructor profile
export const updateInstructorProfile = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const { id } = req.params;
  const updateData = req.validatedData;

  // Check authorization
  if (userId !== id && req.user!.role !== UserRole.ADMIN) {
    throw new AppError('You can only update your own profile', 403);
  }

  const instructorIndex = mockInstructors.findIndex(i => i.id === id);
  if (instructorIndex === -1) {
    throw new AppError('Instructor not found', 404);
  }

  const instructor = mockInstructors[instructorIndex];

  // Update fields
  Object.keys(updateData).forEach(key => {
    if (updateData[key] !== undefined) {
      (instructor as any)[key] = updateData[key];
    }
  });

  instructor.updatedAt = new Date();

  logger.info('Instructor profile updated', { 
    instructorId: instructor.id,
    updatedFields: Object.keys(updateData),
  });

  const response: ApiResponse = {
    success: true,
    message: 'Instructor profile updated successfully',
    data: {
      instructor,
    },
  };

  res.json(response);
});

// Delete instructor profile
export const deleteInstructorProfile = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const { id } = req.params;

  // Check authorization
  if (userId !== id && req.user!.role !== UserRole.ADMIN) {
    throw new AppError('You can only delete your own profile', 403);
  }

  const instructorIndex = mockInstructors.findIndex(i => i.id === id);
  if (instructorIndex === -1) {
    throw new AppError('Instructor not found', 404);
  }

  mockInstructors.splice(instructorIndex, 1);

  logger.info('Instructor profile deleted', { instructorId: id });

  const response: ApiResponse = {
    success: true,
    message: 'Instructor profile deleted successfully',
  };

  res.json(response);
});

// Toggle instructor availability
export const toggleAvailability = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const { id } = req.params;

  // Check authorization
  if (userId !== id && req.user!.role !== UserRole.ADMIN) {
    throw new AppError('You can only update your own availability', 403);
  }

  const instructorIndex = mockInstructors.findIndex(i => i.id === id);
  if (instructorIndex === -1) {
    throw new AppError('Instructor not found', 404);
  }

  const instructor = mockInstructors[instructorIndex];
  instructor.isAvailable = !instructor.isAvailable;
  instructor.updatedAt = new Date();

  logger.info('Instructor availability toggled', { 
    instructorId: instructor.id,
    isAvailable: instructor.isAvailable,
  });

  const response: ApiResponse = {
    success: true,
    message: `Instructor ${instructor.isAvailable ? 'enabled' : 'disabled'} successfully`,
    data: {
      instructor,
    },
  };

  res.json(response);
});

// Get instructor statistics
export const getInstructorStats = asyncHandler(async (req: Request, res: Response) => {
  const { id } = req.params;

  const instructor = mockInstructors.find(i => i.id === id);
