  cancelBooking,
  confirmBooking,
  completeBooking,
} from '@/controllers/bookingController';
import { authenticateToken, authorizeRole } from '@/middleware/auth';
import { UserRole } from '@/types';
import {
  validateRequest,
  validateParams,
  validateQuery,
  bookingCreationSchema,
  bookingUpdateSchema,
  paginationSchema,
  uuidSchema,
} from '@/utils/validation';

const router = Router();

// All booking routes require authentication
router.use(authenticateToken);

// Create booking (clients only)
router.post('/', authorizeRole(UserRole.CLIENT), validateRequest(bookingCreationSchema), createBooking);

// Get bookings (filtered by user role)
router.get('/', validateQuery(paginationSchema), getBookings);

// Get booking by ID
router.get('/:id', validateParams(Joi.object({ id: uuidSchema })), getBookingById);

// Update booking
