                  </div>
                  <div className="flex items-center space-x-1">
                    <ThumbsUp className="w-4 h-4" />
                    <span>{post.like_count}</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <MessageCircle className="w-4 h-4" />
                    <span>{post.reply_count} replies</span>
                  </div>
                  <div className="flex items-center space-x-1">
                    <span>Last activity: {new Date(post.last_activity).toLocaleDateString()}</span>
                  </div>
                </div>

                <div className="flex items-center space-x-2">
                  <Button variant="ghost" size="sm">
                    <Bookmark className="w-4 h-4" />
                  </Button>
                  <Button variant="ghost" size="sm">
                    <Flag className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </div>
          </Card>
        ))}
      </div>

      {/* Empty State */}
      {filteredPosts.length === 0 && (
        <div className="text-center py-12">
          <div className="mx-auto w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mb-4">
            <MessageSquare className="w-6 h-6 text-gray-400" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2">No posts found</h3>
          <p className="text-gray-600 mb-4">
            {searchQuery 
              ? `No posts match your search for "${searchQuery}"`
              : "Be the first to start a discussion in this category"
            }
          </p>
          <Button onClick={() => setShowNewPostModal(true)}>
            <Plus className="w-4 h-4 mr-2" />
            Create First Post
          </Button>
        </div>
      )}

      {/* New Post Modal (placeholder) */}
      {showNewPostModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[90vh] overflow-y-auto">
            <h3 className="text-lg font-semibold mb-4">Create New Post</h3>
            <p className="text-gray-600 mb-4">
              New post modal would be implemented here with category selection, title, content editor, tags, and post type selection.
            </p>
            <div className="flex justify-end space-x-3">
              <Button 
                variant="outline" 
                onClick={() => setShowNewPostModal(false)}
              >
                Cancel
              </Button>
              <Button onClick={() => setShowNewPostModal(false)}>
                Create Post
              </Button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

