const fs = require('fs');
const path = require('path');
const dbConnection = require('../config/connection');

class Migrator {
  constructor() {
    this.migrationsPath = path.join(__dirname, '.');
    this.migrationsTable = 'schema_migrations';
  }

  async init() {
    await dbConnection.connect();
    await this.createMigrationsTable();
  }

  async createMigrationsTable() {
    const query = `
      CREATE TABLE IF NOT EXISTS ${this.migrationsTable} (
        id SERIAL PRIMARY KEY,
        filename VARCHAR(255) NOT NULL UNIQUE,
        executed_at TIMESTAMPTZ DEFAULT NOW()
      );
    `;
    await dbConnection.query(query);
  }

  async getExecutedMigrations() {
    const result = await dbConnection.query(
      `SELECT filename FROM ${this.migrationsTable} ORDER BY id`
    );
    return result.rows.map(row => row.filename);
  }

  async getPendingMigrations() {
    const executed = await this.getExecutedMigrations();
    const allMigrations = fs.readdirSync(this.migrationsPath)
      .filter(file => file.endsWith('.sql') && file !== 'migrator.js')
      .sort();

    return allMigrations.filter(migration => !executed.includes(migration));
  }

  async executeMigration(filename) {
    const filePath = path.join(this.migrationsPath, filename);
    const sql = fs.readFileSync(filePath, 'utf8');

    await dbConnection.transaction(async (client) => {
      await client.query(sql);
      await client.query(
        `INSERT INTO ${this.migrationsTable} (filename) VALUES ($1)`,
        [filename]
      );
    });

    console.log(`Migration ${filename} executed successfully`);
  }

  async migrate() {
    const pendingMigrations = await this.getPendingMigrations();

    if (pendingMigrations.length === 0) {
      console.log('No pending migrations');
      return;
    }

    console.log(`Executing ${pendingMigrations.length} migrations...`);

    for (const migration of pendingMigrations) {
      await this.executeMigration(migration);
    }

    console.log('All migrations completed');
  }

  async rollback() {
    const result = await dbConnection.query(
      `SELECT filename FROM ${this.migrationsTable} ORDER BY id DESC LIMIT 1`
    );

    if (result.rows.length === 0) {
      console.log('No migrations to rollback');
      return;
    }

    const lastMigration = result.rows[0].filename;
    console.log(`Rolling back migration: ${lastMigration}`);

    // For simplicity, we'll just remove the record
    // In a real system, you'd have down migrations
    await dbConnection.query(
      `DELETE FROM ${this.migrationsTable} WHERE filename = $1`,
      [lastMigration]
    );

    console.log('Rollback completed - manual database cleanup required');
  }

  async status() {
    const executed = await this.getExecutedMigrations();
    const pending = await this.getPendingMigrations();

    console.log('Migration Status:');
    console.log(`Executed: ${executed.length}`);
    console.log(`Pending: ${pending.length}`);

    if (pending.length > 0) {
      console.log('\\nPending migrations:');
      pending.forEach(migration => console.log(`  - ${migration}`));
    }
  }
}

module.exports = Migrator;

// CLI usage
if (require.main === module) {
  const migrator = new Migrator();
  const command = process.argv[2];

  (async () => {
    try {
      await migrator.init();

      switch (command) {
        case 'migrate':
          await migrator.migrate();
          break;
        case 'rollback':
          await migrator.rollback();
          break;
        case 'status':
          await migrator.status();
          break;
        default:
          console.log('Usage: node migrator.js [migrate|rollback|status]');
      }
    } catch (error) {
      console.error('Migration error:', error);
    } finally {
      await dbConnection.disconnect();
    }
  })();
}

